// pages/MyReviews.js
import { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';

// Mock data - in a real app, this would come from an API
const mockMovies = [
  { id: 1, title: 'Inception', year: 2010, genre: 'Sci-Fi' },
  { id: 2, title: 'The Shawshank Redemption', year: 1994, genre: 'Drama' },
  { id: 3, title: 'The Dark Knight', year: 2008, genre: 'Action' },
  { id: 4, title: 'Pulp Fiction', year: 1994, genre: 'Crime' },
  { id: 5, title: 'Forrest Gump', year: 1994, genre: 'Drama' },
];

export default function MyReviews() {
  const [reviews, setReviews] = useState([]);
  const [editingReview, setEditingReview] = useState(null);
  const [showReviewForm, setShowReviewForm] = useState(false);
  const [formData, setFormData] = useState({
    movieId: '',
    rating: '',
    review: '',
    title: ''
  });

  // Load reviews from localStorage on component mount
  useEffect(() => {
    const savedReviews = JSON.parse(localStorage.getItem('userReviews') || '[]');
    setReviews(savedReviews);
  }, []);

  // Save reviews to localStorage whenever reviews change
  useEffect(() => {
    localStorage.setItem('userReviews', JSON.stringify(reviews));
  }, [reviews]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (editingReview) {
      // Update existing review
      setReviews(prev => prev.map(review => 
        review.id === editingReview.id 
          ? { ...review, ...formData, lastUpdated: new Date().toISOString() }
          : review
      ));
    } else {
      // Create new review
      const newReview = {
        id: Date.now(),
        ...formData,
        createdAt: new Date().toISOString(),
        lastUpdated: new Date().toISOString()
      };
      setReviews(prev => [...prev, newReview]);
    }

    // Reset form
    setFormData({ movieId: '', rating: '', review: '', title: '' });
    setEditingReview(null);
    setShowReviewForm(false);
  };

  const handleEdit = (review) => {
    setEditingReview(review);
    setFormData({
      movieId: review.movieId,
      rating: review.rating,
      review: review.review,
      title: review.title
    });
    setShowReviewForm(true);
  };

  const handleDelete = (reviewId) => {
    if (window.confirm('Are you sure you want to delete this review?')) {
      setReviews(prev => prev.filter(review => review.id !== reviewId));
    }
  };

  const handleCancel = () => {
    setFormData({ movieId: '', rating: '', review: '', title: '' });
    setEditingReview(null);
    setShowReviewForm(false);
  };

  const getMovieTitle = (movieId) => {
    const movie = mockMovies.find(m => m.id === parseInt(movieId));
    return movie ? movie.title : 'Unknown Movie';
  };

  const renderStars = (rating) => {
    return '‚≠ê'.repeat(rating) + '‚òÜ'.repeat(5 - rating);
  };

  return (
    <div className="my-reviews-page">
      <style>
        {`
          .my-reviews-page {
            max-width: 1000px;
            margin: 0 auto;
          }

          .page-header {
            display: flex;
            justify-content: space-between;
            align-items: center;
            margin-bottom: 2rem;
          }

          .page-title {
            color: #333;
            font-size: 2.5rem;
            font-weight: 700;
          }

          .btn {
            padding: 12px 24px;
            border: none;
            border-radius: 8px;
            font-weight: 600;
    font-size: 1rem;
            cursor: pointer;
            transition: all 0.3s ease;
            text-decoration: none;
            display: inline-flex;
            align-items: center;
            gap: 8px;
          }

          .btn-primary {
            background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
            color: white;
          }

