        <div className="review-form">
          <h2>{editingReview ? 'Edit Review' : 'Write New Review'}</h2>
          <form onSubmit={handleSubmit}>
            <div className="form-group">
              <label className="form-label">Movie</label>
              <select
                className="form-select"
                name="movieId"
                value={formData.movieId}
                onChange={handleInputChange}
                required
              >
                <option value="">Select a movie</option>
                {mockMovies.map(movie => (
                  <option key={movie.id} value={movie.id}>
                    {movie.title} ({movie.year}) - {movie.genre}
                  </option>
                ))}
              </select>
            </div>

            <div className="form-group">
              <label className="form-label">Review Title</label>
              <input
                type="text"
                className="form-control"
                name="title"
                value={formData.title}
                onChange={handleInputChange}
                placeholder="Give your review a title..."
                required
              />
            </div>

            <div className="form-group">
              <label className="form-label">Rating</label>
              <div className="rating-stars">
                {[1, 2, 3, 4, 5].map(star => (
                  <button
                    key={star}
                    type="button"
                    className="star-btn"
                    onClick={() => setFormData(prev => ({ ...prev, rating: star }))}
                  >
                    {star <= formData.rating ? '‚≠ê' : '‚òÜ'}
                  </button>
                ))}
              </div>
              <input
                type="hidden"
                name="rating"
                value={formData.rating}
                required
              />
            </div>

            <div className="form-group">
              <label className="form-label">Review</label>
              <textarea
                className="form-control"
                name="review"
                rows="6"
                value={formData.review}
                onChange={handleInputChange}
                placeholder="Share your thoughts about the movie..."
                required
              />
            </div>

            <div className="form-actions">
              <button type="button" className="btn btn-secondary" onClick={handleCancel}>
                Cancel
              </button>
              <button type="submit" className="btn btn-success">
                {editingReview ? 'Update Review' : 'Publish Review'}
              </button>
            </div>
          </form>
        </div>
      )}

      <div className="reviews-grid">
        {reviews.length === 0 ? (
          <div className="empty-state">
            <div className="empty-state-icon">üìù</div>
            <h3>No Reviews Yet</h3>
            <p>Start sharing your movie opinions by writing your first review!</p>
            <button 
              className="btn btn-primary"
              onClick={() => setShowReviewForm(true)}
              style={{ marginTop: '1rem' }}
            >
              Write Your First Review
            </button>
          </div>
        ) : (
          reviews.map(review => (
            <div key={review.id} className="review-card">
              <div className="review-header">
                <div>
                  <div className="review-movie">
                    {getMovieTitle(review.movieId)}
                  </div>
                  <div className="review-title">{review.title}</div>
                  <div className="review-rating">
                    {renderStars(parseInt(review.rating))} ({review.rating}/5)
                  </div>
                </div>
              </div>

              <div className="review-content">
                {review.review}
              </div>

              <div className="review-footer">
                <div className="review-date">
                  Last updated: {new Date(review.lastUpdated).toLocaleDateString()}
                </div>
                <div className="review-actions">
                  <button 
                    className="btn btn-secondary"
                    onClick={() => handleEdit(review)}
                  >
                    Edit
                  </button>
                  <button 
                    className="btn btn-danger"
                    onClick={() => handleDelete(review.id)}
                  >
                    Delete
                  </button>
                </div>
              </div>
            </div>
