import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

const BACKEND_URL = process.env.REACT_APP_BACKEND_URL || 'http://localhost:5000';
const CURRENT_USER_ID = 'demo'; // ← MATCHES YOUR FIRESTORE DATA

export default function MyReviews() {
  const [reviews, setReviews] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    async function loadMyReviews() {
      try {
        setLoading(true);
        setError('');
        const res = await axios.get(`${BACKEND_URL}/api/reviews/user/${CURRENT_USER_ID}`);
        setReviews(res.data);
      } catch (err) {
        console.error('Failed to load reviews:', err);
        setError('Failed to load your reviews. Is backend running?');
        setReviews([]);
      } finally {
        setLoading(false);
      }
    }

    loadMyReviews();

    // Auto-refresh every 10 seconds
    const interval = setInterval(loadMyReviews, 10000);
    return () => clearInterval(interval);
  }, []);

  const remove = async (id) => {
    if (!window.confirm('Delete this review?')) return;

    try {
      await axios.delete(`${BACKEND_URL}/api/reviews/${id}`);
      setReviews(reviews.filter(r => r.id !== id));
    } catch (err) {
      alert('Failed to delete review.');
    }
  };

  // Safe date formatting
  const formatDate = (timestamp) => {
    if (!timestamp) return 'Recently';
    try {
      if (timestamp.toDate) {
        return timestamp.toDate().toLocaleDateString();
      }
      return 'Recently';
    } catch (error) {
      return 'Recently';
    }
  };

  if (loading) return <div className="my-reviews-loading">Loading your reviews...</div>;
  if (error) return <div className="my-reviews-error">{error}</div>;

  return (
    <>
      <style>
        {`
          .my-reviews-container {
            max-width: 1200px;
            margin: 0 auto;
            padding: 40px 20px;
            font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
          }

          .my-reviews-header {
            text-align: center;
            margin-bottom: 40px;
            color: #2c3e50;
          }

          .my-reviews-title {
            font-size: 2.5rem;
            font-weight: 700;
            margin-bottom: 10px;
            background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
            -webkit-background-clip: text;
            -webkit-text-fill-color: transparent;
          }

          .my-reviews-loading {
            text-align: center;
            padding: 60px 20px;
            font-size: 1.2rem;
            color: #666;
          }

          .my-reviews-error {
            background: linear-gradient(135deg, #ff6b6b, #ee5a24);
            color: white;
            padding: 20px;
            border-radius: 12px;
            text-align: center;
            margin: 20px;
            font-weight: 600;
          }

          .my-reviews-empty {
            text-align: center;
            padding: 60px 20px;
            color: #666;
          }

          .my-reviews-empty h3 {
            margin-bottom: 15px;
            color: #2c3e50;
          }

          .search-link {
            display: inline-block;
            background: linear-gradient(45deg, #667eea, #764ba2);
            color: white;
            padding: 12px 30px;
            border-radius: 8px;
            text-decoration: none;
            font-weight: 600;
            margin-top: 15px;
            transition: transform 0.3s ease;
          }

          .search-link:hover {
            transform: translateY(-2px);
            color: white;
          }

          .reviews-grid {
            display: grid;
            grid-template-columns: repeat(auto-fill, minmax(500px, 1fr));
            gap: 25px;
            margin-top: 30px;
          }

          @media (max-width: 768px) {
            .reviews-grid {
              grid-template-columns: 1fr;
            }
          }

          .review-card {
            background: white;
            border-radius: 16px;
            padding: 25px;
            box-shadow: 0 8px 25px rgba(0, 0, 0, 0.1);
            border: 1px solid #e9ecef;
            transition: all 0.3s ease;
            display: flex;
            flex-direction: column;
            height: 100%;
          }

          .review-card:hover {
            transform: translateY(-5px);
            box-shadow: 0 15px 35px rgba(0, 0, 0, 0.15);
          }

          .review-header {
            display: flex;
            justify-content: space-between;
            align-items: flex-start;
            margin-bottom: 15px;
          }

          .movie-title {
            font-size: 1.3rem;
            font-weight: 700;
            color: #2c3e50;
            text-decoration: none;
            flex: 1;
            margin-right: 15px;
          }

          .movie-title:hover {
            color: #667eea;
          }

          .rating-stars {
            color: #ffd700;
            font-size: 1.1rem;
            white-space: nowrap;
          }

          .review-text {
            color: #555;
            line-height: 1.6;
            margin-bottom: 20px;
            flex: 1;
          }

          .review-footer {
            display: flex;
            justify-content: space-between;
            align-items: center;
            margin-top: auto;
            padding-top: 15px;
            border-top: 1px solid #f1f3f4;
          }

          .review-date {
            color: #888;
            font-size: 0.9rem;
          }

          .delete-btn {
            background: linear-gradient(45deg, #ff6b6b, #ee5a24);
            color: white;
            border: none;
            padding: 8px 16px;
            border-radius: 6px;
            cursor: pointer;
            font-size: 0.9rem;
            font-weight: 600;
            transition: all 0.3s ease;
          }

          .delete-btn:hover {
            transform: translateY(-2px);
            box-shadow: 0 4px 12px rgba(255, 107, 107, 0.4);
          }

          /* Animation for cards */
          @keyframes fadeInUp {
            from {
              opacity: 0;
              transform: translateY(30px);
            }
            to {
              opacity: 1;
              transform: translateY(0);
            }
          }

          .review-card {
            animation: fadeInUp 0.6s ease-out;
          }
        `}
      </style>

      <div className="my-reviews-container">
        <div className="my-reviews-header">
          <h1 className="my-reviews-title">My Reviews</h1>
          <p style={{color: '#666', fontSize: '1.1rem'}}>User: {CURRENT_USER_ID}</p>
        </div>

        {reviews.length === 0 ? (
          <div className="my-reviews-empty">
            <h3>No reviews yet</h3>
            <p>Search movies and share your thoughts with the community!</p>
            <Link to="/search" className="search-link">
              Search Movies
            </Link>
          </div>
        ) : (
          <div className="reviews-grid">
            {reviews.map((r, index) => (
              <div 
                className="review-card" 
                key={r.id}
                style={{animationDelay: `${index * 0.1}s`}}
              >
                <div className="review-header">
                  <Link to={`/movie/${r.movieId}`} className="movie-title">
                    {r.movieTitle}
                  </Link>
                  <span className="rating-stars">
                    {'★'.repeat(r.rating)}{'☆'.repeat(5 - r.rating)}
                  </span>
                </div>

                <p className="review-text">{r.text}</p>

                <div className="review-footer">
                  <span className="review-date">
                    {formatDate(r.createdAt)}
                  </span>
                  <button
                    className="delete-btn"
                    onClick={() => remove(r.id)}
                  >
                    Delete Review
                  </button>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </>
  );
}
