import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams, Link } from 'react-router-dom';

// Use .env (MUST be in project root)
const TMDB_API_KEY = process.env.REACT_APP_TMDB_API_KEY;

export default function Details() {
  const { id } = useParams();
  const [movie, setMovie] = useState(null);
  const [reviews, setReviews] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    // Validate API key
    if (!TMDB_API_KEY) {
      setError('TMDB API key missing. Add REACT_APP_TMDB_API_KEY to .env file.');
      setLoading(false);
      return;
    }

    async function loadData() {
      try {
        setLoading(true);
        setError('');

        // Fetch movie + reviews in parallel
        const [movieRes, reviewRes] = await Promise.all([
          axios.get(`https://api.themoviedb.org/3/movie/${id}?api_key=${TMDB_API_KEY}`),
          axios.get(`${process.env.REACT_APP_BACKEND_URL || 'http://localhost:5000'}/api/reviews/${id}`).catch(() => ({ data: [] }))
        ]);

        setMovie(movieRes.data);
        setReviews(reviewRes.data || []);
      } catch (err) {
        console.error('Load error:', err);
        if (err.response?.status === 401) {
          setError('Invalid TMDB API key. Check .env file.');
        } else if (err.response?.status === 404) {
          setError('Movie not found.');
        } else {
          setError('Failed to load movie. Please try again.');
        }
      } finally {
        setLoading(false);
      }
    }

    loadData();
  }, [id]);

  // Loading
  if (loading) {
    return (
      <div className="container py-5 text-center">
        <div className="spinner-border text-primary" role="status">
          <span className="visually-hidden">Loading...</span>
        </div>
        <p className="mt-3">Loading movie details...</p>
      </div>
    );
  }

  // Error
  if (error) {
    return (
      <div className="container py-5">
        <div className="alert alert-danger text-center">
          <strong>Error:</strong> {error}
        </div>
      </div>
    );
  }

  // No movie
  if (!movie) {
    return <div className="container py-5 text-center">Movie not found.</div>;
  }

  return (
    <div className="container py-4">
      {/* Movie Header */}
      <div className="row mb-5">
        <div className="col-md-4">
          {movie.poster_path ? (
            <img
              src={`https://image.tmdb.org/t/p/w500${movie.poster_path}`}
              alt={movie.title}
              className="img-fluid rounded shadow"
            />
          ) : (
            <div
              className="bg-light d-flex align-items-center justify-content-center rounded shadow"
              style={{ height: '500px' }}
            >
              <span className="text-muted fs-3">No Image</span>
            </div>
          )}
        </div>
        <div className="col-md-8">
          <h1 className="display-5 fw-bold">{movie.title}</h1>
          {movie.tagline && <p className="text-muted fst-italic">{movie.tagline}</p>}
          <p><strong>Release:</strong> {movie.release_date || 'Unknown'}</p>
          <p><strong>TMDB Rating:</strong> {movie.vote_average}/10 ({movie.vote_count} votes)</p>
          <p>{movie.overview || 'No overview available.'}</p>

          {/* Correct Link to AddReview */}
          <Link
            to={`/add-review?movieId=${id}&movieTitle=${encodeURIComponent(movie.title)}`}
            className="btn btn-primary btn-lg"
          >
            Write a Review
          </Link>
        </div>
      </div>

      <hr className="my-5" />

      {/* Reviews Section */}
      <h3 className="mb-4">User Reviews ({reviews.length})</h3>
      {reviews.length === 0 ? (
        <div className="text-center py-5 text-muted">
          <p>No reviews yet. Be the first to review!</p>
          <Link to={`/add-review?movieId=${id}&movieTitle=${encodeURIComponent(movie.title)}`} className="btn btn-outline-primary">
            Add Your Review
          </Link>
        </div>
      ) : (
        <div className="row g-4">
          {reviews.map((r) => (
            <div className="col-md-6" key={r.id}>
              <div className="card h-100 shadow-sm">
                <div className="card-body">
                  <div className="d-flex justify-content-between align-items-start mb-2">
                    <h6 className="mb-0 fw-bold">{r.userName || 'Anonymous'}</h6>
                    <span className="text-warning">
                      {'★'.repeat(r.rating)}{'☆'.repeat(5 - r.rating)}
                    </span>
                  </div>
                  <p className="card-text">{r.text}</p>
                </div>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}
