import React, { useState } from 'react';
import axios from 'axios';
import { useLocation } from 'react-router-dom';

export default function AddReview() {
  // Get movie info from URL query params
  const location = useLocation();
  const queryParams = new URLSearchParams(location.search);
  const movieIdFromSearch = queryParams.get("movieId") || "";
  const movieTitleFromSearch = queryParams.get("movieTitle") || "";

  // State
  const [movieId, setMovieId] = useState(movieIdFromSearch);
  const [movieTitle, setMovieTitle] = useState(movieTitleFromSearch);
  const [rating, setRating] = useState(5);
  const [text, setText] = useState('');
  const [userId, setUserId] = useState('demo-user'); // replace with auth UID in real app
  const [userName, setUserName] = useState('Demo User');

  // Submit review
  const submit = async (e) => {
    e.preventDefault();
    try {
      const payload = { movieId, movieTitle, rating: Number(rating), text, userId, userName };
      await axios.post(
        `${process.env.REACT_APP_BACKEND_URL || 'http://localhost:5000'}/api/reviews`,
        payload
      );
      alert('Review submitted!');
      setRating(5);
      setText('');
    } catch (err) {
      console.error('Error submitting review:', err);
      alert('Failed to submit review.');
    }
  };

  return (
    <div>
      <h2>Add Review</h2>
      <form onSubmit={submit}>
        <div className="mb-3">
          <label className="form-label">Movie ID</label>
          <input
            value={movieId}
            className="form-control"
            readOnly
          />
        </div>
        <div className="mb-3">
          <label className="form-label">Movie Title</label>
          <input
            value={movieTitle}
            className="form-control"
            readOnly
          />
        </div>
        <div className="mb-3">
          <label className="form-label">Rating (1-5)</label>
          <input
            type="number"
            value={rating}
            min="1"
            max="5"
            onChange={(e) => setRating(e.target.value)}
            className="form-control"
            required
          />
        </div>
        <div className="mb-3">
          <label className="form-label">Review</label>
          <textarea
            value={text}
            onChange={(e) => setText(e.target.value)}
            className="form-control"
            rows="4"
            required
          />
        </div>
        <div className="mb-3">
          <label className="form-label">Your Name</label>
          <input
            value={userName}
            onChange={(e) => setUserName(e.target.value)}
            className="form-control"
            required
          />
        </div>
        <button className="btn btn-primary" type="submit">Submit Review</button>
      </form>
    </div>
  );
}
