import React, {useEffect, useState} from 'react';
import axios from 'axios';
import { useParams, Link } from 'react-router-dom';

const TMDB_API_KEY = "PUT_YOUR_TMDB_API_KEY_HERE";

export default function Details(){
  const { id } = useParams();
  const [movie, setMovie] = useState(null);
  const [reviews, setReviews] = useState([]);

  useEffect(()=>{
    async function load(){
      const res = await axios.get(`https://api.themoviedb.org/3/movie/${id}?api_key=${TMDB_API_KEY}`);
      setMovie(res.data);
    }
    load();
    // load reviews from backend
    axios.get(`${process.env.REACT_APP_BACKEND_URL || 'http://localhost:5000'}/api/reviews/${id}`)
      .then(r=>setReviews(r.data))
      .catch(()=>setReviews([]));
  },[id]);

  if(!movie) return <div>Loading...</div>;
  return (
    <div>
      <h2>{movie.title}</h2>
      <p>{movie.overview}</p>
      <p><strong>Release:</strong> {movie.release_date}</p>
      <Link className="btn btn-primary mb-3" to="/add">Add Review</Link>
      <h4>Reviews</h4>
      {reviews.length===0 && <p>No reviews yet.</p>}
      <ul className="list-group">
        {reviews.map(r=>(
          <li className="list-group-item" key={r.id}>
            <strong>{r.userName || 'Anonymous'}</strong> - {r.rating}/5
            <p>{r.text}</p>
          </li>
        ))}
      </ul>
    </div>
  );
}
