// pages/MyReviews.js
import { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';

// Mock movies data - in a real app, this would come from props or API
const mockMovies = [
  { id: 1, title: 'Inception', year: 2010, genre: 'Sci-Fi', director: 'Christopher Nolan' },
  { id: 2, title: 'The Shawshank Redemption', year: 1994, genre: 'Drama', director: 'Frank Darabont' },
  { id: 3, title: 'The Dark Knight', year: 2008, genre: 'Action', director: 'Christopher Nolan' },
  { id: 4, title: 'Pulp Fiction', year: 1994, genre: 'Crime', director: 'Quentin Tarantino' },
  { id: 5, title: 'Forrest Gump', year: 1994, genre: 'Drama', director: 'Robert Zemeckis' },
];

export default function MyReviews() {
  const [reviews, setReviews] = useState([]);
  const [editingReview, setEditingReview] = useState(null);
  const [showReviewForm, setShowReviewForm] = useState(false);
  const [formData, setFormData] = useState({
    movieId: '',
    rating: 0,
    review: '',
    title: ''
  });
  const [allReviews, setAllReviews] = useState([]); // For reading others' reviews

  // Load user's reviews from localStorage
  useEffect(() => {
    const savedReviews = localStorage.getItem('userReviews');
    console.log('Loading user reviews:', savedReviews);

    if (savedReviews) {
      try {
        const parsedReviews = JSON.parse(savedReviews);
        setReviews(Array.isArray(parsedReviews) ? parsedReviews : []);
      } catch (error) {
        console.error('Error parsing reviews:', error);
        setReviews([]);
      }
    } else {
      setReviews([]);
    }

    // Load all reviews (for reading others' reviews)
    const allSavedReviews = localStorage.getItem('allReviews');
    if (allSavedReviews) {
      try {
        const parsedAllReviews = JSON.parse(allSavedReviews);
        setAllReviews(Array.isArray(parsedAllReviews) ? parsedAllReviews : []);
      } catch (error) {
        console.error('Error parsing all reviews:', error);
        setAllReviews([]);
      }
    }
  }, []);

  // Save reviews to localStorage
  useEffect(() => {
    localStorage.setItem('userReviews', JSON.stringify(reviews));
    console.log('Saving user reviews:', reviews);
  }, [reviews]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Submitting form:', formData);

    if (editingReview) {
      // Update existing review
      const updatedReview = {
        ...editingReview,
        ...formData,
        rating: parseInt(formData.rating),
        lastUpdated: new Date().toISOString()
      };

      setReviews(prev => prev.map(review => 
        review.id === editingReview.id ? updatedReview : review
      ));

      // Also update in all reviews
      const updatedAllReviews = allReviews.map(review =>
        review.id === editingReview.id ? updatedReview : review
      );
      setAllReviews(updatedAllReviews);
      localStorage.setItem('allReviews', JSON.stringify(updatedAllReviews));

    } else {
      // Create new review
      const newReview = {
        id: Date.now(),
        movieId: parseInt(formData.movieId),
        title: formData.title,
        rating: parseInt(formData.rating),
        review: formData.review,
        author: 'You', // Current user
        userId: 'current-user', // In real app, this would be actual user ID
        createdAt: new Date().toISOString(),
        lastUpdated: new Date().toISOString()
      };

      setReviews(prev => [...prev, newReview]);

      // Also add to all reviews
      const newAllReviews = [...allReviews, newReview];
      setAllReviews(newAllReviews);
      localStorage.setItem('allReviews', JSON.stringify(newAllReviews));
    }

    // Reset form
    resetForm();
  };

  const handleEdit = (review) => {
    console.log('Editing review:', review);
    setEditingReview(review);
    setFormData({
      movieId: review.movieId.toString(),
      rating: review.rating.toString(),
      review: review.review,
      title: review.title
    });
    setShowReviewForm(true);
  };

  const handleDelete = (reviewId) => {
    if (window.confirm('Are you sure you want to delete this review?')) {
      setReviews(prev => prev.filter(review => review.id !== reviewId));

      // Also remove from all reviews
      const updatedAllReviews = allReviews.filter(review => review.id !== reviewId);
      setAllReviews(updatedAllReviews);
      localStorage.setItem('allReviews', JSON.stringify(updatedAllReviews));
    }
  };

  const handleCancel = () => {
    resetForm();
  };

  const resetForm = () => {
    setFormData({ movieId: '', rating: 0, review: '', title: '' });
    setEditingReview(null);
    setShowReviewForm(false);
  };

  const getMovieDetails = (movieId) => {
    const movie = mockMovies.find(m => m.id === parseInt(movieId));
    return movie || { title: 'Unknown Movie', year: '', genre: '' };
  };

  const renderStars = (rating) => {
    const numericRating = parseInt(rating);
    return '‚≠ê'.repeat(numericRating) + '‚òÜ'.repeat(5 - numericRating);
  };

  // Filter out user's reviews from all reviews to show others' reviews
  const othersReviews = allReviews.filter(review => review.userId !== 'current-user');

  return (
    <div className="my-reviews-page">
      <div className="page-header">
        <h1 className="page-title">My Reviews</h1>
        <button 
          className="btn btn-primary"
          onClick={() => setShowReviewForm(true)}
        >
          <span>‚úèÔ∏è</span>
          Write New Review
        </button>
      </div>

      {/* Create/Edit Review Form */}
      {showReviewForm && (
        <div className="review-form">
          <h2>{editingReview ? 'Edit Your Review' : 'Write New Review'}</h2>
          <form onSubmit={handleSubmit}>
            <div className="form-group">
              <label className="form-label">Select Movie *</label>
              <select
                className="form-select"
                name="movieId"
                value={formData.movieId}
                onChange={handleInputChange}
                required
                disabled={!!editingReview} // Can't change movie when editing
              >
                <option value="">Choose a movie...</option>
                {mockMovies.map(movie => (
                  <option key={movie.id} value={movie.id}>
                    {movie.title} ({movie.year}) - {movie.genre}
                  </option>
                ))}
              </select>
              {editingReview && (
                <small className="text-muted">Cannot change movie for existing review</small>
              )}
            </div>

            <div className="form-group">
              <label className="form-label">Review Title *</label>
              <input
                type="text"
                className="form-control"
                name="title"
                value={formData.title}
                onChange={handleInputChange}
                placeholder="Give your review a compelling title..."
                required
              />
            </div>

            <div className="form-group">
              <label className="form-label">Your Rating *</label>
              <div className="rating-stars">
                {[1, 2, 3, 4, 5].map(star => (
                  <button
                    key={star}
                    type="button"
                    className="star-btn"
                    onClick={() => setFormData(prev => ({ ...prev, rating: star }))}
                  >
                    {star <= formData.rating ? '‚≠ê' : '‚òÜ'}
                  </button>
                ))}
              </div>
              <div className="selected-rating">
                {formData.rating > 0 ? `Selected: ${formData.rating} out of 5 stars` : 'Click stars to rate'}
              </div>
              <input
                type="hidden"
                name="rating"
                value={formData.rating}
                required
              />
            </div>

            <div className="form-group">
              <label className="form-label">Your Review *</label>
              <textarea
                className="form-control"
                name="review"
                rows="6"
                value={formData.review}
                onChange={handleInputChange}
                placeholder="Share your detailed thoughts about the movie... What did you like? What could be better? Would you recommend it?"
                required
              />
            </div>

            <div className="form-actions">
              <button type="button" className="btn btn-secondary" onClick={handleCancel}>
                Cancel
              </button>
              <button type="submit" className="btn btn-success">
                {editingReview ? 'Update Review' : 'Publish Review'}
              </button>
            </div>
          </form>
        </div>
      )}

      {/* User's Reviews Section */}
      <section className="reviews-section">
        <h2 style={{ marginBottom: '1.5rem', color: '#333' }}>Your Reviews ({reviews.length})</h2>

        {reviews.length === 0 ? (
          <div className="empty-state">
            <div className="empty-state-icon">üìù</div>
            <h3>No Reviews Yet</h3>
            <p>Start sharing your movie opinions by writing your first review!</p>
            <button 
              className="btn btn-primary"
              onClick={() => setShowReviewForm(true)}
              style={{ marginTop: '1rem' }}
            >
              Write Your First Review
            </button>
          </div>
        ) : (
          <div className="reviews-grid">
            {reviews.map(review => {
              const movie = getMovieDetails(review.movieId);
              return (
                <div key={review.id} className="review-card">
                  <div className="review-header">
                    <div>
                      <div className="review-movie">
                        {movie.title} ({movie.year})
                        <span className="movie-genre" style={{marginLeft: '10px'}}>
                          {movie.genre}
                        </span>
                      </div>
                      <div className="review-title">{review.title}</div>
                      <div className="review-rating">
                        {renderStars(review.rating)} ({review.rating}/5)
                      </div>
                    </div>
                  </div>

                  <div className="review-content">
                    {review.review}
                  </div>

                  <div className="review-footer">
                    <div className="review-date">
                      Created: {new Date(review.createdAt).toLocaleDateString()} ‚Ä¢ 
                      Updated: {new Date(review.lastUpdated).toLocaleDateString()}
                    </div>
                    <div className="review-actions">
                      <button 
                        className="btn btn-secondary btn-sm"
                        onClick={() => handleEdit(review)}
                      >
                        ‚úèÔ∏è Edit
                      </button>
                      <button 
                        className="btn btn-danger btn-sm"
                        onClick={() => handleDelete(review.id)}
                      >
                        üóëÔ∏è Delete
                      </button>
                    </div>
                  </div>
                </div>
              );
            })}
          </div>
        )}
      </section>

      {/* Others' Reviews Section */}
      <section className="reviews-section" style={{ marginTop: '3rem' }}>
        <h2 style={{ marginBottom: '1.5rem', color: '#333' }}>Other Users' Reviews ({othersReviews.length})</h2>

        {othersReviews.length === 0 ? (
          <div className="empty-state">
            <div className="empty-state-icon">üë•</div>
            <h3>No Other Reviews Yet</h3>
            <p>Be the first to share your thoughts on movies!</p>
          </div>
        ) : (
          <div className="reviews-grid">
            {othersReviews.map(review => {
              const movie = getMovieDetails(review.movieId);
              return (
                <div key={review.id} className="review-card">
                  <div className="review-header">
                    <div>
                      <div className="review-movie">
                        {movie.title} ({movie.year})
                        <span className="movie-genre" style={{marginLeft: '10px'}}>
                          {movie.genre}
                        </span>
                      </div>
                      <div className="review-title">{review.title}</div>
                      <div className="review-rating">
                        {renderStars(review.rating)} ({review.rating}/5)
                      </div>
                    </div>
                  </div>

                  <div className="review-content">
                    {review.review}
                  </div>

                  <div className="review-footer">
                    <div>
                      <div className="review-author">By {review.author || 'Anonymous'}</div>
                      <div className="review-date">
                        Posted: {new Date(review.createdAt).toLocaleDateString()}
                      </div>
                    </div>
                  </div>
                </div>
              );
            })}
          </div>
        )}
      </section>

      {/* Debug Info - Remove in production */}
      <div className="debug-info">
        <details>
          <summary>Debug Info (Click to expand)</summary>
          <div style={{ marginTop: '1rem' }}>
            <p><strong>Your Reviews:</strong> {reviews.length}</p>
            <p><strong>All Reviews:</strong> {allReviews.length}</p>
            <p><strong>Others' Reviews:</strong> {othersReviews.length}</p>
            <button 
              onClick={() => {
                console.log('Your Reviews:', reviews);
                console.log('All Reviews:', allReviews);
                console.log('Others Reviews:', othersReviews);
              }}
              className="btn btn-sm btn-secondary"
            >
              Log to Console
            </button>
            <button 
              onClick={() => {
                localStorage.removeItem('userReviews');
                localStorage.removeItem('allReviews');
                window.location.reload();
              }}
              className="btn btn-sm btn-warning"
              style={{ marginLeft: '10px' }}
            >
              Clear All Data
            </button>
          </div>
        </details>
      </div>
    </div>
  );
}
