/* src/pages/MyReviews.js */
import React, { useEffect, useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import {
  collection,
  addDoc,
  getDocs,
  updateDoc,
  deleteDoc,
  doc,
  query,
  where,
  orderBy,
  serverTimestamp,
  getDoc,
} from "firebase/firestore";
import { onAuthStateChanged } from "firebase/auth";
import { db, auth } from "../firebase-config";   // ← create this file

/* -------------------------------------------------
   Star Rating Helper (keeps your ★/☆ look)
   ------------------------------------------------- */
const StarRating = ({ rating, onChange, readonly = false }) => {
  const [hover, setHover] = useState(0);
  return (
    <div className="rating-stars" style={{ display: "inline-block" }}>
      {[1, 2, 3, 4, 5].map((i) => (
        <span
          key={i}
          style={{
            cursor: readonly ? "default" : "pointer",
            color: i <= (hover || rating) ? "#ffd700" : "#ccc",
            fontSize: "1.1rem",
          }}
          onClick={() => !readonly && onChange(i)}
          onMouseEnter={() => !readonly && setHover(i)}
          onMouseLeave={() => !readonly && setHover(0)}
        >
          {i <= (hover || rating) ? "★" : "☆"}
        </span>
      ))}
    </div>
  );
};

/* -------------------------------------------------
   Review Card (used on Home & MyReviews)
   ------------------------------------------------- */
const ReviewCard = ({ review, showActions = false }) => {
  const navigate = useNavigate();
  const [user, setUser] = useState(null);

  useEffect(() => {
    const unsub = onAuthStateChanged(auth, setUser);
    return unsub;
  }, []);

  const isOwner = user?.uid === review.userId;

  const handleDelete = async () => {
    if (!window.confirm("Delete this review?")) return;
    await deleteDoc(doc(db, "reviews", review.id));
    window.location.reload();               // simple refresh – you can also filter state
  };

  const handleEdit = () => navigate(`/edit-review/${review.id}`);

  return (
    <div className="review-card">
      <div className="review-header">
        <Link
          to={`/movie/${review.movieId || review.restaurantId || ""}`}
          className="movie-title"
        >
          {review.title}
        </Link>
        <StarRating rating={review.rating} readonly />
      </div>

      <p className="review-text">{review.comment}</p>

      <div className="review-footer">
        <span className="review-date">
          {review.createdAt?.toDate?.().toLocaleDateString() ?? "Recently"}
        </span>

        {showActions && isOwner && (
          <>
            <button className="delete-btn" onClick={handleDelete}>
              Delete Review
            </button>
            <button
              style={{
                background: "linear-gradient(45deg,#667eea,#764ba2)",
                color: "white",
                border: "none",
                padding: "8px 16px",
                borderRadius: "6px",
                cursor: "pointer",
                marginLeft: "8px",
              }}
              onClick={handleEdit}
            >
              Edit
            </button>
          </>
        )}
      </div>
    </div>
  );
};

/* -------------------------------------------------
   MyReviews – shows *your* reviews + edit/delete
   ------------------------------------------------- */
export default function MyReviews() {
  const [reviews, setReviews] = useState([]);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    const unsub = onAuthStateChanged(auth, (user) => {
      if (!user) {
        navigate("/");                 // not logged in → go home
        return;
      }

      const q = query(
        collection(db, "reviews"),
        where("userId", "==", user.uid),
        orderBy("createdAt", "desc")
      );

      getDocs(q).then((snap) => {
        setReviews(snap.docs.map((d) => ({ id: d.id, ...d.data() })));
        setLoading(false);
      });
    });

    return unsub;
  }, [navigate]);

  if (loading) return <div className="my-reviews-loading">Loading your reviews…</div>;

  return (
    <>
      <style>
        {`
          .my-reviews-container {max-width:1200px;margin:0 auto;padding:40px 20px;font-family:'Segoe UI',sans-serif;}
          .my-reviews-header {text-align:center;margin-bottom:40px;color:#2c3e50;}
          .my-reviews-title {font-size:2.5rem;font-weight:700;margin-bottom:10px;background:linear-gradient(135deg,#667eea 0%,#764ba2 100%);-webkit-background-clip:text;-webkit-text-fill-color:transparent;}
          .my-reviews-loading,.my-reviews-error {text-align:center;padding:60px 20px;font-size:1.2rem;color:#666;}
          .my-reviews-empty {text-align:center;padding:60px 20px;color:#666;}
          .my-reviews-empty h3 {margin-bottom:15px;color:#2c3e50;}
          .search-link {display:inline-block;background:linear-gradient(45deg,#667eea,#764ba2);color:white;padding:12px 30px;border-radius:8px;text-decoration:none;font-weight:600;margin-top:15px;transition:transform .3s;}
          .search-link:hover {transform:translateY(-2px);color:white;}
          .reviews-grid {display:grid;grid-template-columns:repeat(auto-fill,minmax(500px,1fr));gap:25px;margin-top:30px;}
          @media (max-width:768px){.reviews-grid{grid-template-columns:1fr;}}
          .review-card {background:white;border-radius:16px;padding:25px;box-shadow:0 8px 25px rgba(0,0,0,.1);border:1px solid #e9ecef;transition:all .3s;display:flex;flex-direction:column;height:100%;}
          .review-card:hover{transform:translateY(-5px);box-shadow:0 15px 35px rgba(0,0,0,.15);}
          .review-header {display:flex;justify-content:space-between;align-items:flex-start;margin-bottom:15px;}
          .movie-title {font-size:1.3rem;font-weight:700;color:#2c3e50;text-decoration:none;flex:1;margin-right:15px;}
          .movie-title:hover{color:#667eea;}
          .rating-stars {white-space:nowrap;}
          .review-text {color:#555;line-height:1.6;margin-bottom:20px;flex:1;}
          .review-footer {display:flex;justify-content:space-between;align-items:center;margin-top:auto;padding-top:15px;border-top:1px solid #f1f3f4;}
          .review-date {color:#888;font-size:.9rem;}
          .delete-btn {background:linear-gradient(45deg,#ff6b6b,#ee5a24);color:white;border:none;padding:8px 16px;border-radius:6px;cursor:pointer;font-size:.9rem;font-weight:600;transition:all .3s;}
          .delete-btn:hover{transform:translateY(-2px);box-shadow:0 4px 12px rgba(255,107,107,.4);}
          @keyframes fadeInUp{from{opacity:0;transform:translateY(30px);}to{opacity:1;transform:translateY(0);}}
          .review-card{animation:fadeInUp .6s ease-out;}
        `}
      </style>

      <div className="my-reviews-container">
        <div className="my-reviews-header">
          <h1 className="my-reviews-title">My Reviews</h1>
          <p style={{ color: "#666", fontSize: "1.1rem" }}>
            Logged in as: {auth.currentUser?.displayName || auth.currentUser?.email}
          </p>
        </div>

        {reviews.length === 0 ? (
          <div className="my-reviews-empty">
            <h3>No reviews yet</h3>
            <p>Search movies or restaurants and share your thoughts!</p>
            <Link to="/search" className="search-link">
              Search Movies / Restaurants
            </Link>
          </div>
        ) : (
          <div className="reviews-grid">
            {reviews.map((r, i) => (
              <div
                className="review-card"
                key={r.id}
                style={{ animationDelay: `${i * 0.1}s` }}
              >
                <ReviewCard review={r} showActions />
              </div>
            ))}
          </div>
        )}
      </div>
    </>
  );
}

/* -------------------------------------------------
   Add Review Page (replaces your old AddReview)
   ------------------------------------------------- */
export function AddReview() {
  const [title, setTitle] = useState("");
  const [name, setName] = useState("");
  const [type, setType] = useState("movie"); // movie | restaurant
  const [rating, setRating] = useState(0);
  const [comment, setComment] = useState("");
  const [saving, setSaving] = useState(false);
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (rating === 0) return alert("Please give a rating");
    setSaving(true);
    try {
      await addDoc(collection(db, "reviews"), {
        title,
        name,
        type,
        rating,
        comment,
        userId: auth.currentUser.uid,
        username: auth.currentUser.displayName || auth.currentUser.email,
        createdAt: serverTimestamp(),
        // optional IDs for linking back to movie/restaurant
        movieId: type === "movie" ? name : null,
        restaurantId: type === "restaurant" ? name : null,
      });
      navigate("/my-reviews");
    } catch (err) {
      alert(err.message);
    }
    setSaving(false);
  };

  if (!auth.currentUser) return <p>Please log in to add a review.</p>;

  return (
    <div style={{ maxWidth: 600, margin: "0 auto", padding: "2rem" }}>
      <h1>Add Review</h1>
      <form onSubmit={handleSubmit}>
        <div style={{ marginBottom: "1rem" }}>
          <label>Title</label>
          <input
            type="text"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            required
            style={{ width: "100%", padding: "10px" }}
          />
        </div>

        <div style={{ marginBottom: "1rem" }}>
          <label>Movie / Restaurant Name</label>
          <input
            type="text"
            value={name}
            onChange={(e) => setName(e.target.value)}
            required
            style={{ width: "100%", padding: "10px" }}
          />
        </div>

        <div style={{ marginBottom: "1rem" }}>
          <label>Type</label>
          <select
            value={type}
            onChange={(e) => setType(e.target.value)}
            style={{ width: "100%", padding: "10px" }}
          >
            <option value="movie">Movie</option>
            <option value="restaurant">Restaurant</option>
          </select>
        </div>

        <div style={{ marginBottom: "1rem" }}>
          <label>Rating</label>
          <StarRating rating={rating} onChange={setRating} />
        </div>

        <div style={{ marginBottom: "1rem" }}>
          <label>Comment</label>
          <textarea
            value={comment}
            onChange={(e) => setComment(e.target.value)}
            required
            rows={4}
            style={{ width: "100%", padding: "10px" }}
          />
        </div>

        <button
          type="submit"
          disabled={saving}
          style={{
            background: "linear-gradient(45deg,#667eea,#764ba2)",
            color: "white",
            padding: "12px 24px",
            border: "none",
            borderRadius: "8px",
          }}
        >
          {saving ? "Saving…" : "Submit Review"}
        </button>
      </form>
    </div>
  );
}

/* -------------------------------------------------
   Edit Review Page (new route /edit-review/:id)
   ------------------------------------------------- */
export function EditReview() {
  const { id } = React.useParams();
  const [review, setReview] = useState(null);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    const fetch = async () => {
      const snap = await getDoc(doc(db, "reviews", id));
      if (snap.exists() && snap.data().userId === auth.currentUser?.uid) {
        setReview({ id: snap.id, ...snap.data() });
      } else {
        navigate("/my-reviews");
      }
      setLoading(false);
    };
    fetch();
  }, [id, navigate]);

  const handleUpdate = async (e) => {
    e.preventDefault();
    await updateDoc(doc(db, "reviews", id), {
      title: review.title,
      name: review.name,
      rating: review.rating,
      comment: review.comment,
    });
    navigate("/my-reviews");
  };

  if (loading) return <p>Loading…</p>;
  if (!review) return <p>Not found.</p>;

  return (
    <div style={{ maxWidth: 600, margin: "0 auto", padding: "2rem" }}>
      <h1>Edit Review</h1>
      <form onSubmit={handleUpdate}>
        <div style={{ marginBottom: "1rem" }}>
          <label>Title</label>
          <input
            type="text"
            value={review.title}
            onChange={(e) => setReview({ ...review, title: e.target.value })}
            required
            style={{ width: "100%", padding: "10px" }}
          />
        </div>

        <div style={{ marginBottom: "1rem" }}>
          <label>Name</label>
          <input
            type="text"
            value={review.name}
            onChange={(e) => setReview({ ...review, name: e.target.value })}
            required
            style={{ width: "100%", padding: "10px" }}
          />
        </div>

        <div style={{ marginBottom: "1rem" }}>
          <label>Rating</label>
          <StarRating
            rating={review.rating}
            onChange={(r) => setReview({ ...review, rating: r })}
          />
        </div>

        <div style={{ marginBottom: "1rem" }}>
          <label>Comment</label>
          <textarea
            value={review.comment}
            onChange={(e) => setReview({ ...review, comment: e.target.value })}
            required
            rows={4}
            style={{ width: "100%", padding: "10px" }}
          />
        </div>

        <button
          type="submit"
          style={{
            background: "linear-gradient(45deg,#667eea,#764ba2)",
            color: "white",
            padding: "12px 24px",
            border: "none",
            borderRadius: "8px",
          }}
        >
          Update Review
        </button>
      </form>
    </div>
  );
}

/* -------------------------------------------------
   Home – shows *all* reviews (read others' reviews)
   ------------------------------------------------- */
export function Home() {
  const [reviews, setReviews] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const q = query(collection(db, "reviews"), orderBy("createdAt", "desc"));
    getDocs(q).then((snap) => {
      setReviews(snap.docs.map((d) => ({ id: d.id, ...d.data() })));
      setLoading(false);
    });
  }, []);

  if (loading) return <p>Loading all reviews…</p>;

  return (
    <div style={{ padding: "2rem", maxWidth: 1200, margin: "0 auto" }}>
      <h1>All Reviews</h1>
      {reviews.length === 0 ? (
        <p>No reviews yet – be the first!</p>
      ) : (
        <div className="reviews-grid" style={{ marginTop: "2rem" }}>
          {reviews.map((r) => (
            <ReviewCard key={r.id} review={r} />
          ))}
        </div>
      )}
    </div>
  );
}
