import { BrowserRouter, Routes, Route, Link, useParams, useNavigate } from "react-router-dom";
import { useState, useEffect } from "react";
import { db, auth } from "./firebase-config"; // Make sure you have this file
import {
  collection,
  addDoc,
  getDocs,
  updateDoc,
  deleteDoc,
  doc,
  query,
  where,
  orderBy,
  serverTimestamp,
} from "firebase/firestore";
import { onAuthStateChanged } from "firebase/auth";

// Star Rating Component
const StarRating = ({ rating, onRatingChange, readonly = false }) => {
  const [hover, setHover] = useState(0);

  return (
    <div className="star-rating">
      {[1, 2, 3, 4, 5].map((star) => (
        <span
          key={star}
          className={`star ${star <= (hover || rating) ? "filled" : ""}`}
          onClick={() => !readonly && onRatingChange(star)}
          onMouseEnter={() => !readonly && setHover(star)}
          onMouseLeave={() => !readonly && setHover(0)}
          style={{
            fontSize: "1.8rem",
            cursor: readonly ? "default" : "pointer",
            color: star <= (hover || rating) ? "#ffd700" : "#ccc",
          }}
        >
          ★
        </span>
      ))}
    </div>
  );
};

// Home Page - List All Reviews
const Home = () => {
  const [reviews, setReviews] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchReviews = async () => {
      const q = query(collection(db, "reviews"), orderBy("createdAt", "desc"));
      const snapshot = await getDocs(q);
      const data = snapshot.docs.map((doc) => ({ id: doc.id, ...doc.data() }));
      setReviews(data);
      setLoading(false);
    };
    fetchReviews();
  }, []);

  if (loading) return <p>Loading reviews...</p>;

  return (
    <div>
      <h1>All Reviews</h1>
      {reviews.length === 0 ? (
        <p>No reviews yet. Be the first to review!</p>
      ) : (
        <div className="reviews-grid">
          {reviews.map((review) => (
            <ReviewCard key={review.id} review={review} />
          ))}
        </div>
      )}
    </div>
  );
};

// Review Card Component
const ReviewCard = ({ review, showActions = true }) => {
  const [currentUser, setCurrentUser] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const unsubscribe = onAuthStateChanged(auth, (user) => setCurrentUser(user));
    return unsubscribe;
  }, []);

  const isOwner = currentUser?.uid === review.userId;

  return (
    <div className="review-card" style={cardStyle}>
      <h3>{review.title}</h3>
      <p>
        <strong>{review.type === "movie" ? "Movie" : "Restaurant"}:</strong> {review.name}
      </p>
      <StarRating rating={review.rating} readonly />
      <p>{review.comment}</p>
      <p>
        <small>By: {review.username} • {new Date(review.createdAt?.toDate()).toLocaleDateString()}</small>
      </p>
      {showActions && isOwner && (
        <div className="review-actions">
          <button onClick={() => navigate(`/edit-review/${review.id}`)} className="btn-edit">
            Edit
          </button>
          <button onClick={() => handleDelete(review.id)} className="btn-delete">
            Delete
          </button>
        </div>
      )}
    </div>
  );
};

// Add Review Page
const AddReview = () => {
  const [title, setTitle] = useState("");
  const [name, setName] = useState("");
  const [type, setType] = useState("movie");
  const [rating, setRating] = useState(0);
  const [comment, setComment] = useState("");
  const [loading, setLoading] = useState(false);
  const [user, setUser] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const unsubscribe = onAuthStateChanged(auth, (u) => {
      if (!u) navigate("/login");
      else setUser(u);
    });
    return unsubscribe;
  }, [navigate]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!user || rating === 0) return;

    setLoading(true);
    try {
      await addDoc(collection(db, "reviews"), {
        title,
        name,
        type,
        rating,
        comment,
        userId: user.uid,
        username: user.displayName || user.email.split("@")[0],
        createdAt: serverTimestamp(),
      });
      navigate("/my-reviews");
    } catch (err) {
      alert("Error adding review: " + err.message);
    }
    setLoading(false);
  };

  if (!user) return <p>Please log in to add a review.</p>;

  return (
    <div>
      <h1>Add Review</h1>
      <form onSubmit={handleSubmit} style={{ maxWidth: 600, margin: "0 auto" }}>
        <div style={formGroup}>
          <label>Title</label>
          <input
            type="text"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            required
            style={inputStyle}
          />
        </div>

        <div style={formGroup}>
          <label>Movie or Restaurant Name</label>
          <input
            type="text"
            value={name}
            onChange={(e) => setName(e.target.value)}
            required
            style={inputStyle}
          />
        </div>

        <div style={formGroup}>
          <label>Type</label>
          <select value={type} onChange={(e) => setType(e.target.value)} style={inputStyle}>
            <option value="movie">Movie</option>
            <option value="restaurant">Restaurant</option>
          </select>
        </div>

        <div style={formGroup}>
          <label>Rating</label>
          <StarRating rating={rating} onRatingChange={setRating} />
        </div>

        <div style={formGroup}>
          <label>Comment</label>
          <textarea
            value={comment}
            onChange={(e) => setComment(e.target.value)}
            required
            rows="4"
            style={inputStyle}
          />
        </div>

        <button type="submit" disabled={loading} style={btnStyle}>
            {loading ? "Saving..." : "Submit Review"}
          </button>
      </form>
    </div>
  );
};

// Edit Review Page
const EditReview = () => {
  const { id } = useParams();
  const [review, setReview] = useState(null);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchReview = async () => {
      const docRef = doc(db, "reviews", id);
      const docSnap = await getDoc(docRef);
      if (docSnap.exists()) {
        const data = { id: docSnap.id, ...docSnap.data() };
        if (data.userId !== auth.currentUser?.uid) {
          navigate("/my-reviews");
          return;
        }
        setReview(data);
      }
      setLoading(false);
    };
    fetchReview();
  }, [id, navigate]);

  const handleUpdate = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      await updateDoc(doc(db, "reviews", id), {
        title: review.title,
        name: review.name,
        rating: review.rating,
        comment: review.comment,
      });
      navigate("/my-reviews");
    } catch (err) {
      alert("Error updating: " + err.message);
    }
    setLoading(false);
  };

  if (loading) return <p>Loading...</p>;
  if (!review) return <p>Review not found.</p>;

  return (
    <div>
      <h1>Edit Review</h1>
      <form onSubmit={handleUpdate} style={{ maxWidth: 600, margin: "0 auto" }}>
        {/* Same form fields as AddReview, but with value={review.xxx} */}
        {/* ... (copy from AddReview, just change value and onChange) */}
      </form>
    </div>
  );
};

// My Reviews Page
const MyReviews = () => {
  const [reviews, setReviews] = useState([]);
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const unsubscribe = onAuthStateChanged(auth, async (u) => {
      setUser(u);
      if (u) {
        const q = query(
          collection(db, "reviews"),
          where("userId", "==", u.uid),
          orderBy("createdAt", "desc")
        );
        const snapshot = await getDocs(q);
        const data = snapshot.docs.map((doc) => ({ id: doc.id, ...doc.data() }));
        setReviews(data);
      }
      setLoading(false);
    });
    return unsubscribe;
  }, []);

  const handleDelete = async (id) => {
    if (!window.confirm("Delete this review?")) return;
    try {
      await deleteDoc(doc(db, "reviews", id));
      setReviews(reviews.filter((r) => r.id !== id));
    } catch (err) {
      alert("Error deleting: " + err.message);
    }
  };

  if (!user) return <p>Please log in to see your reviews.</p>;
  if (loading) return <p>Loading your reviews...</p>;

  return (
    <div>
      <h1>My Reviews</h1>
      {reviews.length === 0 ? (
        <p>You haven't written any reviews yet.</p>
      ) : (
        <div className="reviews-grid">
          {reviews.map((review) => (
            <div key={review.id} className="review-card" style={cardStyle}>
              <h3>{review.title}</h3>
              <p>
                <strong>{review.type}:</strong> {review.name}
              </p>
              <StarRating rating={review.rating} readonly />
              <p>{review.comment}</p>
              <div className="review-actions">
                <button onClick={() => navigate(`/edit-review/${review.id}`)} className="btn-edit">
                  Edit
                </button>
                <button onClick={() => handleDelete(review.id)} className="btn-delete">
                  Delete
                </button>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

// Add missing imports at top
import { getDoc } from "firebase/firestore";

// Add CSS-in-JS styles
const cardStyle = {
  border: "1px solid #ddd",
  borderRadius: "12px",
  padding: "1.5rem",
  marginBottom: "1.5rem",
  background: "white",
  boxShadow: "0 4px 12px rgba(0,0,0,0.05)",
};

const formGroup = { marginBottom: "1.5rem" };
const inputStyle = {
  width: "100%",
  padding: "12px",
  border: "1px solid #ddd",
  borderRadius: "8px",
  fontSize: "1rem",
};
const btnStyle = {
  background: "#667eea",
  color: "white",
  padding: "12px 24px",
  border: "none",
  borderRadius: "8px",
  fontSize: "1.1rem",
  cursor: "pointer",
};

// Update Routes in App()
export default function App() {
  return (
    <>
      <style>{/* Your existing CSS */}</style>

      <BrowserRouter>
        <div className="app-container">
          {/* Your navbar */}
          <nav className="navbar">...</nav>

          <div className="main-content">
            <div className="main-container">
              <Routes>
                <Route path="/" element={<Home />} />
                <Route path="/add-review" element={<AddReview />} />
                <Route path="/my-reviews" element={<MyReviews />} />
                <Route path="/edit-review/:id" element={<EditReview />} />
              </Routes>
            </div>
          </div>

          <footer className="footer">...</footer>
        </div>
      </BrowserRouter>
    </>
  );
}
