import { BrowserRouter, Routes, Route, Link } from "react-router-dom";
import { useState, useEffect } from "react";
import Home from "./pages/Home";
import Search from "./pages/Search";
import Details from "./pages/Details";
import AddReview from "./pages/AddReview";
import MyReviews from "./pages/MyReviews";

// Mock data for movies and initial reviews
const mockMovies = [
  { id: 1, title: 'Inception', year: 2010, genre: 'Sci-Fi', director: 'Christopher Nolan' },
  { id: 2, title: 'The Shawshank Redemption', year: 1994, genre: 'Drama', director: 'Frank Darabont' },
  { id: 3, title: 'The Dark Knight', year: 2008, genre: 'Action', director: 'Christopher Nolan' },
  { id: 4, title: 'Pulp Fiction', year: 1994, genre: 'Crime', director: 'Quentin Tarantino' },
  { id: 5, title: 'Forrest Gump', year: 1994, genre: 'Drama', director: 'Robert Zemeckis' },
];

const initialReviews = [
  {
    id: 1,
    movieId: 1,
    userId: "user1",
    userName: "MovieLover42",
    rating: 5,
    title: "Mind-blowing masterpiece!",
    review: "Christopher Nolan outdid himself with this one. The concept of dream within a dream was executed perfectly.",
    createdAt: "2024-01-15T10:30:00Z",
    lastUpdated: "2024-01-15T10:30:00Z"
  },
  {
    id: 2,
    movieId: 2,
    userId: "user2",
    userName: "CinemaFan99",
    rating: 4,
    title: "A timeless classic",
    review: "The performances are incredible, especially Morgan Freeman. The story of hope and redemption is beautifully told.",
    createdAt: "2024-01-10T14:20:00Z",
    lastUpdated: "2024-01-10T14:20:00Z"
  },
  {
    id: 3,
    movieId: 3,
    userId: "user3",
    userName: "ActionJunkie",
    rating: 5,
    title: "Best superhero movie ever",
    review: "Heath Ledger's Joker performance alone makes this movie worth watching. The action sequences are breathtaking.",
    createdAt: "2024-01-08T09:15:00Z",
    lastUpdated: "2024-01-08T09:15:00Z"
  }
];

export default function App() {
  const [reviews, setReviews] = useState([]);
  const [currentUser, setCurrentUser] = useState({ id: "currentUser", name: "You" });

  // Initialize reviews from localStorage or use initial reviews
  useEffect(() => {
    const savedReviews = localStorage.getItem('movieReviews');
    if (savedReviews) {
      setReviews(JSON.parse(savedReviews));
    } else {
      setReviews(initialReviews);
      localStorage.setItem('movieReviews', JSON.stringify(initialReviews));
    }
  }, []);

  // Save reviews to localStorage whenever they change
  useEffect(() => {
    localStorage.setItem('movieReviews', JSON.stringify(reviews));
  }, [reviews]);

  // Function to add a new review
  const addReview = (reviewData) => {
    const newReview = {
      id: Date.now(),
      ...reviewData,
      userId: currentUser.id,
      userName: currentUser.name,
      createdAt: new Date().toISOString(),
      lastUpdated: new Date().toISOString()
    };
    setReviews(prev => [...prev, newReview]);
  };

  // Function to update a review
  const updateReview = (reviewId, updatedData) => {
    setReviews(prev => prev.map(review => 
      review.id === reviewId 
        ? { ...review, ...updatedData, lastUpdated: new Date().toISOString() }
        : review
    ));
  };

  // Function to delete a review
  const deleteReview = (reviewId) => {
    setReviews(prev => prev.filter(review => review.id !== reviewId));
  };

  // Function to get reviews for a specific movie
  const getMovieReviews = (movieId) => {
    return reviews.filter(review => review.movieId === movieId);
  };

  // Function to get user's reviews
  const getUserReviews = () => {
    return reviews.filter(review => review.userId === currentUser.id);
  };

  return (
    <>
      <style>
        {`
          * {
            margin: 0;
            padding: 0;
            box-sizing: border-box;
          }

          body {
            font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
            background: #f8f9fa;
            color: #333;
            line-height: 1.6;
          }

          .app-container {
            min-height: 100vh;
            display: flex;
            flex-direction: column;
          }

          .navbar {
            background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
            padding: 1rem 0;
            box-shadow: 0 4px 20px rgba(0, 0, 0, 0.1);
            border: none;
          }

          .nav-container {
            max-width: 1200px;
            margin: 0 auto;
            padding: 0 20px;
            display: flex;
            justify-content: space-between;
            align-items: center;
          }

          .navbar-brand {
            font-size: 1.8rem;
            font-weight: 800;
            color: white;
            text-decoration: none;
            display: flex;
            align-items: center;
            gap: 10px;
            transition: all 0.3s ease;
          }

          .navbar-brand:hover {
            color: #ffd700;
            transform: translateY(-1px);
          }

          .nav-links {
            display: flex;
            list-style: none;
            gap: 2rem;
            margin: 0;
            padding: 0;
            align-items: center;
          }

          .nav-link {
            color: white;
            text-decoration: none;
            font-weight: 600;
            font-size: 1.1rem;
            padding: 8px 16px;
            border-radius: 8px;
            transition: all 0.3s ease;
            display: flex;
            align-items: center;
            gap: 8px;
          }

          .nav-link:hover {
            background: rgba(255, 255, 255, 0.2);
            color: #ffd700;
            transform: translateY(-2px);
          }

          .main-content {
            flex: 1;
            padding: 2rem 0;
          }

          .main-container {
            max-width: 1200px;
            margin: 0 auto;
            padding: 0 20px;
          }

          /* Footer Styles */
          .footer {
            background: linear-gradient(135deg, #2c3e50 0%, #34495e 100%);
            color: white;
            text-align: center;
            padding: 2rem 0;
            margin-top: auto;
          }

          .footer-content {
            max-width: 1200px;
            margin: 0 auto;
            padding: 0 20px;
          }

          .footer-links {
            display: flex;
            justify-content: center;
            gap: 2rem;
            margin-top: 1rem;
            flex-wrap: wrap;
          }

          .footer-link {
            color: #ecf0f1;
            text-decoration: none;
            transition: color 0.3s ease;
          }

          .footer-link:hover {
            color: #ffd700;
          }

          /* Common button styles */
          .btn {
            padding: 12px 24px;
            border: none;
            border-radius: 8px;
            font-weight: 600;
            font-size: 1rem;
            cursor: pointer;
            transition: all 0.3s ease;
            text-decoration: none;
            display: inline-flex;
            align-items: center;
            gap: 8px;
          }

          .btn-primary {
            background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
            color: white;
          }

          .btn-primary:hover {
            transform: translateY(-2px);
            box-shadow: 0 4px 15px rgba(102, 126, 234, 0.4);
          }

          .btn-secondary {
            background: #6c757d;
            color: white;
          }

          .btn-secondary:hover {
            background: #5a6268;
          }

          .btn-danger {
            background: #dc3545;
            color: white;
          }

          .btn-danger:hover {
            background: #c82333;
          }

          .btn-success {
            background: #28a745;
            color: white;
          }

          .btn-success:hover {
            background: #218838;
          }

          .btn-warning {
            background: #ffc107;
            color: #212529;
          }

          .btn-warning:hover {
            background: #e0a800;
          }

          .btn-sm {
            padding: 8px 16px;
            font-size: 0.9rem;
          }

          /* Loading animation */
          @keyframes fadeIn {
            from {
              opacity: 0;
            }
            to {
              opacity: 1;
            }
          }

          .main-content {
            animation: fadeIn 0.5s ease-in;
          }

          /* Icon styles */
          .nav-icon {
            font-size: 1.2em;
          }

          /* Mobile Responsive */
          @media (max-width: 768px) {
            .nav-container {
              flex-direction: column;
              gap: 1rem;
              padding: 0 15px;
            }

            .nav-links {
              gap: 1rem;
              flex-wrap: wrap;
              justify-content: center;
            }

            .navbar-brand {
              font-size: 1.5rem;
            }

            .nav-link {
              font-size: 1rem;
              padding: 6px 12px;
            }

            .main-content {
              padding: 1rem 0;
            }

            .footer-links {
              gap: 1rem;
            }
          }

          /* Form styles */
          .form-group {
            margin-bottom: 1.5rem;
          }

          .form-label {
            display: block;
            margin-bottom: 0.5rem;
            font-weight: 600;
            color: #333;
          }

          .form-control {
            width: 100%;
            padding: 12px;
            border: 2px solid #e9ecef;
            border-radius: 8px;
            font-size: 1rem;
            transition: border-color 0.3s ease;
            font-family: inherit;
          }

          .form-control:focus {
            outline: none;
            border-color: #667eea;
            box-shadow: 0 0 0 3px rgba(102, 126, 234, 0.1);
          }

          .form-select {
            width: 100%;
            padding: 12px;
            border: 2px solid #e9ecef;
            border-radius: 8px;
            font-size: 1rem;
            background: white;
            font-family: inherit;
          }

          .rating-stars {
            display: flex;
            gap: 8px;
            margin-top: 0.5rem;
          }

          .star-btn {
            background: none;
            border: none;
            font-size: 2rem;
            cursor: pointer;
            transition: transform 0.2s ease;
            padding: 4px;
          }

          .star-btn:hover {
            transform: scale(1.3);
          }

          .selected-rating {
            margin-top: 0.5rem;
            font-weight: 600;
            color: #667eea;
          }

          .btn-block {
            width: 100%;
          }

          /* Page common styles */
          .page-title {
            color: #333;
            font-size: 2.5rem;
            font-weight: 700;
            margin-bottom: 2rem;
          }

          .page-header {
            display: flex;
            justify-content: space-between;
            align-items: center;
            margin-bottom: 2rem;
            flex-wrap: wrap;
            gap: 1rem;
          }

          .header-actions {
            display: flex;
            gap: 1rem;
            align-items: center;
          }

          /* Review card styles */
          .reviews-grid {
            display: grid;
            gap: 1.5rem;
          }

          .review-card {
            background: white;
            padding: 1.5rem;
            border-radius: 12px;
            box-shadow: 0 2px 15px rgba(0, 0, 0, 0.1);
            transition: transform 0.3s ease, box-shadow 0.3s ease;
            border-left: 4px solid #667eea;
          }

          .review-card:hover {
            transform: translateY(-2px);
            box-shadow: 0 4px 20px rgba(0, 0, 0, 0.15);
          }

          .review-header {
            display: flex;
            justify-content: space-between;
            align-items: flex-start;
            margin-bottom: 1rem;
          }

          .review-movie {
            font-size: 1.3rem;
            font-weight: 700;
            color: #333;
            margin-bottom: 0.5rem;
          }

          .review-title {
            font-size: 1.1rem;
            font-weight: 600;
            color: #667eea;
            margin-bottom: 0.5rem;
          }

          .review-rating {
            font-size: 1.2rem;
            margin-bottom: 1rem;
          }

          .review-content {
            color: #555;
            line-height: 1.6;
            margin-bottom: 1rem;
            white-space: pre-wrap;
          }

          .review-footer {
            display: flex;
            justify-content: space-between;
            align-items: center;
            padding-top: 1rem;
            border-top: 1px solid #e9ecef;
          }

          .review-author {
            color: #6c757d;
            font-weight: 600;
          }

          .review-date {
            color: #6c757d;
            font-size: 0.9rem;
          }

          .review-actions {
            display: flex;
            gap: 0.5rem;
          }

          /* Empty state styles */
          .empty-state {
            text-align: center;
            padding: 3rem;
            color: #6c757d;
            background: white;
            border-radius: 12px;
            box-shadow: 0 2px 15px rgba(0, 0, 0, 0.1);
          }

          .empty-state-icon {
            font-size: 4rem;
            margin-bottom: 1rem;
          }

          /* Form actions */
          .form-actions {
            display: flex;
            gap: 1rem;
            justify-content: flex-end;
            margin-top: 1.5rem;
          }

          /* Review form styles */
          .review-form {
            background: white;
            padding: 2rem;
            border-radius: 12px;
            box-shadow: 0 4px 20px rgba(0, 0, 0, 0.1);
            margin-bottom: 2rem;
            animation: slideDown 0.3s ease;
          }

          @keyframes slideDown {
            from {
              opacity: 0;
              transform: translateY(-20px);
            }
            to {
              opacity: 1;
              transform: translateY(0);
            }
          }

          /* Movie card styles */
          .movies-grid {
            display: grid;
            grid-template-columns: repeat(auto-fill, minmax(300px, 1fr));
            gap: 1.5rem;
          }

          .movie-card {
            background: white;
            border-radius: 12px;
            overflow: hidden;
            box-shadow: 0 2px 15px rgba(0, 0, 0, 0.1);
            transition: transform 0.3s ease, box-shadow 0.3s ease;
          }

          .movie-card:hover {
            transform: translateY(-5px);
            box-shadow: 0 8px 25px rgba(0, 0, 0, 0.15);
          }

          .movie-poster {
            width: 100%;
            height: 200px;
            background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
            display: flex;
            align-items: center;
            justify-content: center;
            color: white;
            font-size: 3rem;
          }

          .movie-info {
            padding: 1.5rem;
          }

          .movie-title {
            font-size: 1.3rem;
            font-weight: 700;
            color: #333;
            margin-bottom: 0.5rem;
          }

          .movie-meta {
            color: #6c757d;
            font-size: 0.9rem;
            margin-bottom: 1rem;
          }

          .movie-actions {
            display: flex;
            gap: 0.5rem;
          }

          /* Average rating */
          .average-rating {
            display: flex;
            align-items: center;
            gap: 0.5rem;
            margin-bottom: 1rem;
          }

          .rating-value {
            font-weight: 600;
            color: #333;
          }

          /* Mobile responsive adjustments */
          @media (max-width: 768px) {
            .page-header {
              flex-direction: column;
              align-items: stretch;
            }

            .header-actions {
              justify-content: space-between;
            }

            .review-header {
              flex-direction: column;
              gap: 1rem;
            }

            .review-footer {
              flex-direction: column;
              gap: 1rem;
              align-items: flex-start;
            }

            .review-actions {
              width: 100%;
              justify-content: flex-start;
            }

            .form-actions {
              flex-direction: column;
            }

            .btn {
              padding: 10px 16px;
              font-size: 0.9rem;
            }

            .page-title {
              font-size: 2rem;
            }

            .movies-grid {
              grid-template-columns: 1fr;
            }
          }
        `}
      </style>

      <BrowserRouter>
        <div className="app-container">
          <nav className="navbar">
            <div className="nav-container">
              <Link className="navbar-brand" to="/">
                <span className="nav-icon">üé¨</span>
                Movie Reviews
              </Link>
              <ul className="nav-links">
                <li>
                  <Link className="nav-link" to="/search">
                    <span className="nav-icon">üîç</span>
                    Search Movies
                  </Link>
                </li>
                <li>
                  <Link className="nav-link" to="/add-review">
                    <span className="nav-icon">‚≠ê</span>
                    Add Review
                  </Link>
                </li>
                <li>
                  <Link className="nav-link" to="/my-reviews">
                    <span className="nav-icon">üìù</span>
                    My Reviews
                  </Link>
                </li>
              </ul>
            </div>
          </nav>

          <div className="main-content">
            <div className="main-container">
              <Routes>
                <Route path="/" element={
                  <Home 
                    movies={mockMovies} 
                    reviews={reviews} 
                    getMovieReviews={getMovieReviews}
                  />
                } />
                <Route path="/search" element={
                  <Search 
                    movies={mockMovies} 
                    reviews={reviews}
                    getMovieReviews={getMovieReviews}
                  />
                } />
                <Route path="/movie/:id" element={
                  <Details 
                    movies={mockMovies} 
                    reviews={reviews}
                    currentUser={currentUser}
                    addReview={addReview}
                    updateReview={updateReview}
                    deleteReview={deleteReview}
                    getMovieReviews={getMovieReviews}
                  />
                } />
                <Route path="/add-review" element={
                  <AddReview 
                    movies={mockMovies} 
                    addReview={addReview}
                    currentUser={currentUser}
                  />
                } />
                <Route path="/my-reviews" element={
                  <MyReviews 
                    movies={mockMovies}
                    reviews={getUserReviews()}
                    currentUser={currentUser}
                    updateReview={updateReview}
                    deleteReview={deleteReview}
                  />
                } />
                <Route path="*" element={<NotFound />} />
              </Routes>
            </div>
          </div>

          <footer className="footer">
            <div className="footer-content">
              <p>&copy; 2024 Movie Review Platform. All rights reserved.</p>
              <div className="footer-links">
                <Link to="/" className="footer-link">Home</Link>
                <Link to="/search" className="footer-link">Search</Link>
                <Link to="/add-review" className="footer-link">Add Review</Link>
                <Link to="/my-reviews" className="footer-link">My Reviews</Link>
              </div>
            </div>
          </footer>
        </div>
      </BrowserRouter>
    </>
  );
}

// Simple 404 component
function NotFound() {
  return (
    <div style={{ textAlign: 'center', padding: '2rem' }}>
      <h2>404 - Page Not Found</h2>
      <p>The page you're looking for doesn't exist.</p>
      <Link to="/" className="btn btn-primary">Go Home</Link>
    </div>
  );
}
