// pages/MyReviews.js
import { useState, useEffect } from 'react';

// Mock data - in a real app, this would come from an API
const mockMovies = [
  { id: 1, title: 'Inception', year: 2010, genre: 'Sci-Fi' },
  { id: 2, title: 'The Shawshank Redemption', year: 1994, genre: 'Drama' },
  { id: 3, title: 'The Dark Knight', year: 2008, genre: 'Action' },
  { id: 4, title: 'Pulp Fiction', year: 1994, genre: 'Crime' },
  { id: 5, title: 'Forrest Gump', year: 1994, genre: 'Drama' },
];

export default function MyReviews() {
  const [reviews, setReviews] = useState([]);
  const [editingReview, setEditingReview] = useState(null);
  const [showReviewForm, setShowReviewForm] = useState(false);
  const [formData, setFormData] = useState({
    movieId: '',
    rating: '',
    review: '',
    title: ''
  });

  // Load reviews from localStorage on component mount
  useEffect(() => {
    try {
      const savedReviews = localStorage.getItem('userReviews');
      console.log('Loaded from localStorage:', savedReviews);

      if (savedReviews) {
        const parsedReviews = JSON.parse(savedReviews);
        setReviews(Array.isArray(parsedReviews) ? parsedReviews : []);
      } else {
        setReviews([]);
      }
    } catch (error) {
      console.error('Error loading reviews from localStorage:', error);
      setReviews([]);
    }
  }, []);

  // Save reviews to localStorage whenever reviews change
  useEffect(() => {
    try {
      localStorage.setItem('userReviews', JSON.stringify(reviews));
      console.log('Saved to localStorage:', reviews);
    } catch (error) {
      console.error('Error saving reviews to localStorage:', error);
    }
  }, [reviews]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Submitting form:', formData);

    if (editingReview) {
      // Update existing review
      setReviews(prev => prev.map(review => 
        review.id === editingReview.id 
          ? { 
              ...review, 
              ...formData, 
              rating: parseInt(formData.rating),
              lastUpdated: new Date().toISOString() 
            }
          : review
      ));
      console.log('Updated review');
    } else {
      // Create new review
      const newReview = {
        id: Date.now(),
        movieId: formData.movieId,
        title: formData.title,
        rating: parseInt(formData.rating),
        review: formData.review,
        createdAt: new Date().toISOString(),
        lastUpdated: new Date().toISOString()
      };
      setReviews(prev => [...prev, newReview]);
      console.log('Created new review:', newReview);
    }

    // Reset form
    setFormData({ movieId: '', rating: '', review: '', title: '' });
    setEditingReview(null);
    setShowReviewForm(false);
  };

  const handleEdit = (review) => {
    console.log('Editing review:', review);
    setEditingReview(review);
    setFormData({
      movieId: review.movieId.toString(),
      rating: review.rating.toString(),
      review: review.review,
      title: review.title
    });
    setShowReviewForm(true);
  };

  const handleDelete = (reviewId) => {
    if (window.confirm('Are you sure you want to delete this review?')) {
      setReviews(prev => prev.filter(review => review.id !== reviewId));
      console.log('Deleted review:', reviewId);
    }
  };

  const handleCancel = () => {
    setFormData({ movieId: '', rating: '', review: '', title: '' });
    setEditingReview(null);
    setShowReviewForm(false);
  };

  const getMovieTitle = (movieId) => {
    const movie = mockMovies.find(m => m.id === parseInt(movieId));
    return movie ? `${movie.title} (${movie.year})` : 'Unknown Movie';
  };

  const getMovieDetails = (movieId) => {
    const movie = mockMovies.find(m => m.id === parseInt(movieId));
    return movie ? movie : { title: 'Unknown Movie', year: '', genre: '' };
  };

  const renderStars = (rating) => {
    const numericRating = parseInt(rating);
    return '‚≠ê'.repeat(numericRating) + '‚òÜ'.repeat(5 - numericRating);
  };

  // Debug function to clear all reviews
  const clearAllReviews = () => {
    if (window.confirm('Are you sure you want to delete ALL reviews? This cannot be undone.')) {
      setReviews([]);
      localStorage.removeItem('userReviews');
    }
  };

  return (
    <div className="my-reviews-page">
      <style>
        {`
          .my-reviews-page {
            max-width: 1000px;
            margin: 0 auto;
          }

          .page-header {
            display: flex;
            justify-content: space-between;
            align-items: center;
            margin-bottom: 2rem;
            flex-wrap: wrap;
            gap: 1rem;
          }

          .page-title {
            color: #333;
            font-size: 2.5rem;
            font-weight: 700;
            margin: 0;
          }

          .header-actions {
            display: flex;
            gap: 1rem;
            align-items: center;
          }

          .btn {
            padding: 12px 24px;
            border: none;
            border-radius: 8px;
            font-weight: 600;
            font-size: 1rem;
            cursor: pointer;
            transition: all 0.3s ease;
            text-decoration: none;
            display: inline-flex;
            align-items: center;
            gap: 8px;
          }

          .btn-primary {
            background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
            color: white;
          }

          .btn-primary:hover {
            transform: translateY(-2px);
            box-shadow: 0 4px 15px rgba(102, 126, 234, 0.4);
          }

          .btn-secondary {
            background: #6c757d;
            color: white;
          }

          .btn-secondary:hover {
            background: #5a6268;
          }

          .btn-danger {
            background: #dc3545;
            color: white;
          }

          .btn-danger:hover {
            background: #c82333;
          }

          .btn-success {
            background: #28a745;
            color: white;
          }

          .btn-success:hover {
            background: #218838;
          }

          .btn-warning {
            background: #ffc107;
            color: #212529;
          }

          .btn-warning:hover {
            background: #e0a800;
          }

          .review-form {
            background: white;
            padding: 2rem;
            border-radius: 12px;
            box-shadow: 0 4px 20px rgba(0, 0, 0, 0.1);
            margin-bottom: 2rem;
            animation: slideDown 0.3s ease;
          }

          @keyframes slideDown {
            from {
              opacity: 0;
              transform: translateY(-20px);
            }
            to {
              opacity: 1;
              transform: translateY(0);
            }
          }

          .form-group {
            margin-bottom: 1.5rem;
          }

          .form-label {
            display: block;
            margin-bottom: 0.5rem;
            font-weight: 600;
            color: #333;
          }

          .form-control {
            width: 100%;
            padding: 12px;
            border: 2px solid #e9ecef;
            border-radius: 8px;
            font-size: 1rem;
            transition: border-color 0.3s ease;
            font-family: inherit;
          }

          .form-control:focus {
            outline: none;
            border-color: #667eea;
            box-shadow: 0 0 0 3px rgba(102, 126, 234, 0.1);
          }

          .form-select {
            width: 100%;
            padding: 12px;
            border: 2px solid #e9ecef;
            border-radius: 8px;
            font-size: 1rem;
            background: white;
            font-family: inherit;
          }

          .form-select:focus {
            outline: none;
            border-color: #667eea;
            box-shadow: 0 0 0 3px rgba(102, 126, 234, 0.1);
          }

          .rating-stars {
            display: flex;
            gap: 8px;
            margin-top: 0.5rem;
          }

          .star-btn {
            background: none;
            border: none;
            font-size: 2rem;
            cursor: pointer;
            transition: transform 0.2s ease;
            padding: 4px;
          }

          .star-btn:hover {
            transform: scale(1.3);
          }

          .selected-rating {
            margin-top: 0.5rem;
            font-weight: 600;
            color: #667eea;
          }

          .reviews-grid {
            display: grid;
            gap: 1.5rem;
          }

          .review-card {
            background: white;
            padding: 1.5rem;
            border-radius: 12px;
            box-shadow: 0 2px 15px rgba(0, 0, 0, 0.1);
            transition: transform 0.3s ease, box-shadow 0.3s ease;
            border-left: 4px solid #667eea;
          }

          .review-card:hover {
            transform: translateY(-2px);
            box-shadow: 0 4px 20px rgba(0, 0, 0, 0.15);
          }

          .review-header {
            display: flex;
            justify-content: space-between;
            align-items: flex-start;
            margin-bottom: 1rem;
          }

          .review-movie {
            font-size: 1.3rem;
            font-weight: 700;
            color: #333;
            margin-bottom: 0.5rem;
          }

          .movie-genre {
            background: #e9ecef;
            padding: 4px 8px;
            border-radius: 4px;
            font-size: 0.8rem;
            color: #6c757d;
          }

          .review-title {
            font-size: 1.1rem;
            font-weight: 600;
            color: #667eea;
            margin-bottom: 0.5rem;
          }

          .review-rating {
            font-size: 1.2rem;
            margin-bottom: 1rem;
          }

          .review-content {
            color: #555;
            line-height: 1.6;
            margin-bottom: 1rem;
            white-space: pre-wrap;
          }

          .review-footer {
            display: flex;
            justify-content: space-between;
            align-items: center;
            padding-top: 1rem;
            border-top: 1px solid #e9ecef;
          }

          .review-date {
            color: #6c757d;
            font-size: 0.9rem;
          }

          .review-actions {
            display: flex;
            gap: 0.5rem;
          }

          .empty-state {
            text-align: center;
            padding: 3rem;
            color: #6c757d;
            background: white;
            border-radius: 12px;
            box-shadow: 0 2px 15px rgba(0, 0, 0, 0.1);
          }

          .empty-state-icon {
            font-size: 4rem;
            margin-bottom: 1rem;
          }

          .form-actions {
            display: flex;
            gap: 1rem;
            justify-content: flex-end;
            margin-top: 1.5rem;
          }

          .debug-info {
            background: #f8f9fa;
            padding: 1rem;
            border-radius: 8px;
            margin-top: 2rem;
            font-size: 0.9rem;
            color: #6c757d;
          }

          @media (max-width: 768px) {
            .page-header {
              flex-direction: column;
              align-items: stretch;
            }

            .header-actions {
              justify-content: space-between;
            }

            .review-header {
              flex-direction: column;
              gap: 1rem;
            }

            .review-footer {
              flex-direction: column;
              gap: 1rem;
              align-items: flex-start;
            }

            .review-actions {
              width: 100%;
              justify-content: flex-start;
            }

            .form-actions {
              flex-direction: column;
            }

            .btn {
              padding: 10px 16px;
              font-size: 0.9rem;
            }
          }
        `}
      </style>

      <div className="page-header">
        <h1 className="page-title">My Reviews</h1>
        <div className="header-actions">
          <button 
            className="btn btn-primary"
            onClick={() => setShowReviewForm(true)}
          >
            <span>‚úèÔ∏è</span>
            Write New Review
          </button>
          {reviews.length > 0 && (
            <button 
              className="btn btn-warning"
              onClick={clearAllReviews}
            >
              <span>üóëÔ∏è</span>
              Clear All
            </button>
          )}
        </div>
      </div>

      {showReviewForm && (
        <div className="review-form">
          <h2>{editingReview ? 'Edit Review' : 'Write New Review'}</h2>
          <form onSubmit={handleSubmit}>
            <div className="form-group">
              <label className="form-label">Movie *</label>
              <select
                className="form-select"
                name="movieId"
                value={formData.movieId}
                onChange={handleInputChange}
                required
              >
                <option value="">Select a movie</option>
                {mockMovies.map(movie => (
                  <option key={movie.id} value={movie.id}>
                    {movie.title} ({movie.year}) - {movie.genre}
                  </option>
                ))}
              </select>
            </div>

            <div className="form-group">
              <label className="form-label">Review Title *</label>
              <input
                type="text"
                className="form-control"
                name="title"
                value={formData.title}
                onChange={handleInputChange}
                placeholder="Give your review a title..."
                required
              />
            </div>

            <div className="form-group">
              <label className="form-label">Rating *</label>
              <div className="rating-stars">
                {[1, 2, 3, 4, 5].map(star => (
                  <button
                    key={star}
                    type="button"
                    className="star-btn"
                    onClick={() => setFormData(prev => ({ ...prev, rating: star.toString() }))}
                  >
                    {star <= formData.rating ? '‚≠ê' : '‚òÜ'}
                  </button>
                ))}
              </div>
              {formData.rating && (
                <div className="selected-rating">
                  Selected: {formData.rating} out of 5 stars
                </div>
              )}
              <input
                type="hidden"
                name="rating"
                value={formData.rating}
                required
              />
            </div>

            <div className="form-group">
              <label className="form-label">Review Content *</label>
              <textarea
                className="form-control"
                name="review"
                rows="6"
                value={formData.review}
                onChange={handleInputChange}
                placeholder="Share your thoughts about the movie... What did you like? What could be better?"
                required
              />
            </div>

            <div className="form-actions">
              <button type="button" className="btn btn-secondary" onClick={handleCancel}>
                Cancel
              </button>
              <button type="submit" className="btn btn-success">
                {editingReview ? 'Update Review' : 'Publish Review'}
              </button>
            </div>
          </form>
        </div>
      )}

      <div className="reviews-grid">
        {reviews.length === 0 ? (
          <div className="empty-state">
            <div className="empty-state-icon">üìù</div>
            <h3>No Reviews Yet</h3>
            <p>Start sharing your movie opinions by writing your first review!</p>
            <button 
              className="btn btn-primary"
              onClick={() => setShowReviewForm(true)}
              style={{ marginTop: '1rem' }}
            >
              Write Your First Review
            </button>
          </div>
        ) : (
          reviews.map(review => {
            const movie = getMovieDetails(review.movieId);
            return (
              <div key={review.id} className="review-card">
                <div className="review-header">
                  <div>
                    <div className="review-movie">
                      {movie.title} ({movie.year})
                      <span className="movie-genre" style={{marginLeft: '10px'}}>
                        {movie.genre}
                      </span>
                    </div>
                    <div className="review-title">{review.title}</div>
                    <div className="review-rating">
                      {renderStars(review.rating)} ({review.rating}/5)
                    </div>
                  </div>
                </div>

                <div className="review-content">
                  {review.review}
                </div>

                <div className="review-footer">
                  <div className="review-date">
                    Created: {new Date(review.createdAt).toLocaleDateString()} ‚Ä¢ 
                    Updated: {new Date(review.lastUpdated).toLocaleDateString()}
                  </div>
                  <div className="review-actions">
                    <button 
                      className="btn btn-secondary"
                      onClick={() => handleEdit(review)}
                    >
                      Edit
                    </button>
                    <button 
                      className="btn btn-danger"
                      onClick={() => handleDelete(review.id)}
                    >
                      Delete
                    </button>
                  </div>
                </div>
              </div>
            );
          })
        )}
      </div>

      {/* Debug information - remove in production */}
      <div className="debug-info">
        <strong>Debug Info:</strong> {reviews.length} reviews stored | 
        Last updated: {new Date().toLocaleTimeString()} |
        <button 
          onClick={() => console.log('Reviews:', reviews)} 
          style={{marginLeft: '10px', padding: '2px 8px', fontSize: '0.8rem'}}
        >
          Log to Console
        </button>
      </div>
    </div>
  );
}
