import React, { useState } from "react";
import axios from "axios";
import { Link } from "react-router-dom";

// Secure API key from .env
const TMDB_API_KEY = process.env.REACT_APP_TMDB_API_KEY;

export default function Search() {
  const [q, setQ] = useState("");
  const [results, setResults] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  const search = async (e) => {
    e.preventDefault();
    if (!q.trim()) return;

    setLoading(true);
    setError("");
    setResults([]);

    try {
      const url = `https://api.themoviedb.org/3/search/movie?api_key=${TMDB_API_KEY}&query=${encodeURIComponent(q.trim())}`;
      const res = await axios.get(url);

      if (!res.data.results) {
        setError("No movies found.");
      } else {
        setResults(res.data.results);
      }
    } catch (err) {
      console.error("TMDB API Error:", err);
      setError("Failed to search movies. Check your API key or internet connection.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="container py-4">
      <h2 className="mb-4 fw-bold text-center text-primary">Search Movies</h2>

      {/* Search Form */}
      <form onSubmit={search} className="mb-5">
        <div className="input-group input-group-lg shadow-sm">
          <input
            type="text"
            value={q}
            onChange={(e) => setQ(e.target.value)}
            className="form-control rounded-start-pill"
            placeholder="e.g. Inception, Avatar..."
            disabled={loading}
            style={{ borderRight: "none" }}
          />
          <button
            className="btn btn-primary rounded-end-pill px-4"
            type="submit"
            disabled={loading}
          >
            {loading ? (
              <>
                <span className="spinner-border spinner-border-sm me-2" role="status" aria-hidden="true"></span>
                Searching...
              </>
            ) : (
              <>
                <i className="bi bi-search me-2"></i> Search
              </>
            )}
          </button>
        </div>
      </form>

      {/* Error Alert */}
      {error && (
        <div className="alert alert-danger d-flex align-items-center" role="alert">
          <i className="bi bi-exclamation-triangle-fill me-2"></i>
          {error}
        </div>
      )}

      {/* Loading State */}
      {loading && (
        <div className="text-center py-5">
          <div className="spinner-border text-primary" role="status">
            <span className="visually-hidden">Loading...</span>
          </div>
        </div>
      )}

      {/* No Results */}
      {!loading && results.length === 0 && q && !error && (
        <div className="text-center py-5 text-muted">
          <i className="bi bi-film display-1"></i>
          <p className="mt-3">No movies found for "<strong>{q}</strong>"</p>
        </div>
      )}

      {/* Results Grid */}
      <div className="row g-4">
        {results.map((m) => (
          <div className="col-md-6 col-lg-4" key={m.id}>
            <div className="card h-100 shadow-sm hover-shadow transition">
              {/* Poster */}
              {m.poster_path ? (
                <img
                  src={`https://image.tmdb.org/t/p/w342${m.poster_path}`}
                  className="card-img-top"
                  alt={m.title}
                  style={{ height: "400px", objectFit: "cover" }}
                />
              ) : (
                <div
                  className="bg-light d-flex align-items-center justify-content-center"
                  style={{ height: "400px" }}
                >
                  <span className="text-muted fs-4">No Image</span>
                </div>
              )}

              {/* Card Body */}
              <div className="card-body d-flex flex-column">
                <h5 className="card-title fw-bold">{m.title}</h5>
                <p className="card-text text-muted small">
                  {m.release_date ? m.release_date.split("-")[0] : "Unknown Year"}
                </p>

                {/* Buttons */}
                <div className="mt-auto d-grid gap-2 d-md-flex">
                  <Link
                    to={`/movie/${m.id}`}
                    className="btn btn-outline-primary btn-sm flex-fill"
                  >
                    View Details
                  </Link>
                  <Link
                    to={`/add-review?movieId=${m.id}&movieTitle=${encodeURIComponent(m.title)}`}
                    className="btn btn-primary btn-sm flex-fill"
                  >
                    Rate & Review
                  </Link>
                </div>
              </div>
            </div>
          </div>
        ))}
      </div>

      {/* Custom Styles */}
      <style jsx>{`
        .hover-shadow {
          transition: all 0.3s ease;
        }
        .hover-shadow:hover {
          transform: translateY(-5px);
          box-shadow: 0 10px 20px rgba(0, 0, 0, 0.1) !important;
        }
        .input-group:focus-within .form-control,
        .input-group:focus-within .btn {
          box-shadow: 0 0 0 0.25rem rgba(13, 110, 253, 0.25) !important;
        }
      `}</style>
    </div>
  );
}
