import React, { useState, useEffect } from "react";
import axios from "axios";
import { useLocation } from "react-router-dom";

export default function AddReview() {

  // Read movieId & movieTitle from URL (e.g. ?movieId=123&movieTitle=Avatar)
  const { search } = useLocation();
  const params = new URLSearchParams(search);
  const movieIdFromURL = params.get("movieId") || "";
  const movieTitleFromURL = params.get("movieTitle") || "";

  const [movieId, setMovieId] = useState(movieIdFromURL);
  const [movieTitle, setMovieTitle] = useState(movieTitleFromURL);
  const [rating, setRating] = useState(5);
  const [text, setText] = useState("");
  const [userId] = useState("demo-user"); 
  const [userName, setUserName] = useState("Demo User");
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    setMovieId(movieIdFromURL);
    setMovieTitle(movieTitleFromURL);
  }, [movieIdFromURL, movieTitleFromURL]);

  const submit = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      const payload = { movieId, movieTitle, rating: Number(rating), text, userId, userName };

      await axios.post(
        `${process.env.REACT_APP_BACKEND_URL || "http://localhost:5000"}/api/reviews`,
        payload
      );

      alert("✅ Review submitted successfully!");

      setText("");
      setRating(5);

    } catch (err) {
      console.error(err);
      alert("❌ Failed to submit review. Check backend console.");
    }

    setLoading(false);
  };

  return (
    <div className="container mt-4">
      <h2>Add Review</h2>
      <p><strong>Movie:</strong> {movieTitle} ({movieId})</p>

      <form onSubmit={submit}>
        <div className="mb-3">
          <label className="form-label">Rating (1 - 5)</label>
          <input type="number" min="1" max="5" value={rating}
            onChange={(e) => setRating(e.target.value)}
            className="form-control" required />
        </div>

        <div className="mb-3">
          <label className="form-label">Your Review</label>
          <textarea className="form-control"
            rows="4" value={text}
            onChange={(e) => setText(e.target.value)}
            required />
        </div>

        <div className="mb-3">
          <label className="form-label">Your name</label>
          <input className="form-control" value={userName}
            onChange={(e) => setUserName(e.target.value)} />
        </div>

        <button className="btn btn-success" type="submit" disabled={loading}>
          {loading ? "Submitting..." : "Submit Review"}
        </button>
      </form>
    </div>
  );
}
