import { BrowserRouter, Routes, Route, Link } from "react-router-dom";
import Home from "./pages/Home";
import Search from "./pages/Search";
import Details from "./pages/Details";
import AddReview from "./pages/AddReview";
import MyReviews from "./pages/MyReviews";

export default function App() {
  return (
    <>
      <style>
        {`
          * {
            margin: 0;
            padding: 0;
            box-sizing: border-box;
          }

          body {
            font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
            background: #f8f9fa;
            color: #333;
          }

          .app-container {
            min-height: 100vh;
            display: flex;
            flex-direction: column;
          }

          .navbar {
            background: linear-gradient(135deg, #667eea 0%, #764ba2 100%) !important;
            padding: 1rem 0;
            box-shadow: 0 4px 20px rgba(0, 0, 0, 0.1);
            border: none;
          }

          .nav-container {
            max-width: 1200px;
            margin: 0 auto;
            padding: 0 20px;
            display: flex;
            justify-content: space-between;
            align-items: center;
          }

          .navbar-brand {
            font-size: 1.8rem !important;
            font-weight: 800;
            color: white !important;
            text-decoration: none;
            display: flex;
            align-items: center;
            gap: 10px;
          }

          .navbar-brand:hover {
            color: #ffd700 !important;
            transform: translateY(-1px);
          }

          .nav-links {
            display: flex;
            list-style: none;
            gap: 2rem;
            margin: 0;
            padding: 0;
            align-items: center;
          }

          .nav-link {
            color: white !important;
            text-decoration: none;
            font-weight: 600;
            font-size: 1.1rem;
            padding: 8px 16px;
            border-radius: 8px;
            transition: all 0.3s ease;
            display: flex;
            align-items: center;
            gap: 8px;
          }

          .nav-link:hover {
            background: rgba(255, 255, 255, 0.2);
            color: #ffd700 !important;
            transform: translateY(-2px);
          }

          .nav-link.active {
            background: rgba(255, 255, 255, 0.3);
            color: #ffd700 !important;
          }

          .main-content {
            flex: 1;
            padding: 2rem 0;
          }

          .main-container {
            max-width: 1200px;
            margin: 0 auto;
            padding: 0 20px;
          }

          /* Mobile Responsive */
          @media (max-width: 768px) {
            .nav-container {
              flex-direction: column;
              gap: 1rem;
              padding: 0 15px;
            }

            .nav-links {
              gap: 1rem;
              flex-wrap: wrap;
              justify-content: center;
            }

            .navbar-brand {
              font-size: 1.5rem !important;
            }

            .nav-link {
              font-size: 1rem;
              padding: 6px 12px;
            }

            .main-content {
              padding: 1rem 0;
            }
          }

          /* Animation for page transitions */
          .page-enter {
            opacity: 0;
            transform: translateY(20px);
          }

          .page-enter-active {
            opacity: 1;
            transform: translateY(0);
            transition: opacity 0.3s ease, transform 0.3s ease;
          }

          /* Footer Styles */
          .footer {
            background: linear-gradient(135deg, #2c3e50 0%, #34495e 100%);
            color: white;
            text-align: center;
            padding: 2rem 0;
            margin-top: auto;
          }

          .footer-content {
            max-width: 1200px;
            margin: 0 auto;
            padding: 0 20px;
          }

          .footer-links {
            display: flex;
            justify-content: center;
            gap: 2rem;
            margin-top: 1rem;
            flex-wrap: wrap;
          }

          .footer-link {
            color: #ecf0f1;
            text-decoration: none;
            transition: color 0.3s ease;
          }

          .footer-link:hover {
            color: #ffd700;
          }

          /* Loading animation */
          @keyframes fadeIn {
            from {
              opacity: 0;
            }
            to {
              opacity: 1;
            }
          }

          .main-content {
            animation: fadeIn 0.5s ease-in;
          }

          /* Icon styles */
          .nav-icon {
            font-size: 1.2em;
          }
        `}
      </style>

      <BrowserRouter>
        <div className="app-container">
          <nav className="navbar">
            <div className="nav-container">
              <Link className="navbar-brand" to="/">
                <span className="nav-icon">üé¨</span>
                Movie Reviews
              </Link>
              <ul className="nav-links">
                <li>
                  <Link className="nav-link" to="/search">
                    <span className="nav-icon">üîç</span>
                    Search
                  </Link>
                </li>
                <li>
                  <Link className="nav-link" to="/add-review">
                    <span className="nav-icon">‚≠ê</span>
                    Add Review
                  </Link>
                </li>
                <li>
                  <Link className="nav-link" to="/my-reviews">
                    <span className="nav-icon">üìù</span>
                    My Reviews
                  </Link>
                </li>
              </ul>
            </div>
          </nav>

          <div className="main-content">
            <div className="main-container">
              <Routes>
                <Route path="/" element={<Home />} />
                <Route path="/search" element={<Search />} />
                <Route path="/movie/:id" element={<Details />} />
                <Route path="/add-review" element={<AddReview />} />
                <Route path="/my-reviews" element={<MyReviews />} />
              </Routes>
            </div>
          </div>

          <footer className="footer">
            <div className="footer-content">
              <p>&copy; 2024 Movie Review Platform. All rights reserved.</p>
              <div className="footer-links">
                <a href="/" className="footer-link">Home</a>
                <a href="/search" className="footer-link">Search</a>
                <a href="/add-review" className="footer-link">Add Review</a>
                <a href="/my-reviews" className="footer-link">My Reviews</a>
              </div>
            </div>
          </footer>
        </div>
      </BrowserRouter>
    </>
  );
}
