import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

const BACKEND_URL = process.env.REACT_APP_BACKEND_URL || 'http://localhost:5000';
const CURRENT_USER_ID = 'demo'; // ← MATCHES YOUR FIRESTORE DATA

export default function MyReviews() {
  const [reviews, setReviews] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    async function loadMyReviews() {
      try {
        setLoading(true);
        setError('');
        const res = await axios.get(`${BACKEND_URL}/api/reviews/user/${CURRENT_USER_ID}`);
        setReviews(res.data);
      } catch (err) {
        console.error('Failed to load reviews:', err);
        setError('Failed to load your reviews. Is backend running?');
        setReviews([]);
      } finally {
        setLoading(false);
      }
    }

    loadMyReviews();

    // Auto-refresh every 10 seconds
    const interval = setInterval(loadMyReviews, 10000);
    return () => clearInterval(interval);
  }, []);

  const remove = async (id) => {
    if (!window.confirm('Delete this review?')) return;

    try {
      await axios.delete(`${BACKEND_URL}/api/reviews/${id}`);
      setReviews(reviews.filter(r => r.id !== id));
    } catch (err) {
      alert('Failed to delete review.');
    }
  };

  if (loading) return <div className="container py-5 text-center">Loading your reviews...</div>;
  if (error) return <div className="container py-5 alert alert-danger">{error}</div>;

  return (
    <div className="container py-5">
      <h1 className="mb-4">My Reviews (demo user)</h1>

      {reviews.length === 0 ? (
        <p className="text-muted">
          No reviews yet. <Link to="/search">Search a movie</Link> and write one!
        </p>
      ) : (
        <div className="row">
          {reviews.map(r => (
            <div className="col-md-6 mb-4" key={r.id}>
              <div className="card h-100 shadow-sm">
                <div className="card-body d-flex flex-column">
                  <div className="d-flex justify-content-between align-items-start mb-2">
                    <h5 className="card-title mb-0">
                      <Link to={`/movie/${r.movieId}`} className="text-primary text-decoration-none">
                        {r.movieTitle}
                      </Link>
                    </h5>
                    <span className="text-warning">
                      {'★'.repeat(r.rating)}{'☆'.repeat(5 - r.rating)}
                    </span>
                  </div>
                  <p className="flex-grow-1">{r.text}</p>
                  <div className="d-flex justify-content-between align-items-center">
                    <small className="text-muted">
                      {r.createdAt?.toDate?.()?.toLocaleDateString() || 'Just now'}
                    </small>
                    <button
                      className="btn btn-sm btn-outline-danger"
                      onClick={() => remove(r.id)}
                    >
                      Delete
                    </button>
                  </div>
                </div>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}
