import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useLocation, useNavigate } from 'react-router-dom';

export default function AddReview() {
  const location = useLocation();
  const navigate = useNavigate();

  // Extract query params
  const queryParams = new URLSearchParams(location.search);
  const movieIdFromSearch = queryParams.get("movieId") || "";
  const movieTitleFromSearch = queryParams.get("movieTitle") || "";

  // State
  const [movieId] = useState(movieIdFromSearch);
  const [movieTitle] = useState(movieTitleFromSearch);
  const [rating, setRating] = useState(5);
  const [text, setText] = useState('');
  const [userName, setUserName] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  // Focus on user name if empty
  useEffect(() => {
    if (!userName && movieTitle) {
      setUserName(''); // Let user type
    }
  }, [movieTitle, userName]);

  // Submit handler
  const submit = async (e) => {
    e.preventDefault();
    setError('');
    setLoading(true);

    // Validation
    if (!userName.trim()) {
      setError('Please enter your name.');
      setLoading(false);
      return;
    }
    if (!text.trim()) {
      setError('Please write a review.');
      setLoading(false);
      return;
    }

    try {
      const payload = {
        movieId,
        movieTitle,
        rating: Number(rating),
        text: text.trim(),
        userId: 'demo-user', // Replace with real auth later
        userName: userName.trim(),
      };

      await axios.post(
        `${process.env.REACT_APP_BACKEND_URL || 'http://localhost:5000'}/api/reviews`,
        payload
      );

      alert('Review submitted successfully!');
      navigate('/'); // Redirect to home/search after success
    } catch (err) {
      console.error('Submit error:', err);
      setError(err.response?.data?.message || 'Failed to submit review. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  // Star rating click handler
  const handleStarClick = (value) => {
    setRating(value);
  };

  return (
    <div className="container py-5">
      <div className="row justify-content-center">
        <div className="col-md-8 col-lg-6">
          <div className="card shadow-lg border-0 rounded-4 overflow-hidden">
            <div className="card-header bg-gradient text-white text-center py-4" style={{ background: 'linear-gradient(135deg, #007bff, #0056b3)' }}>
              <h3 className="mb-0 fw-bold">Write a Review</h3>
              {movieTitle && <p className="mb-0 mt-2 opacity-90">{movieTitle}</p>}
            </div>

            <div className="card-body p-5">
              {error && (
                <div className="alert alert-danger alert-dismissible fade show" role="alert">
                  {error}
                  <button type="button" className="btn-close" onClick={() => setError('')}></button>
                </div>
              )}

              <form onSubmit={submit}>
                {/* Movie Info (Read-only) */}
                <div className="row mb-4">
                  <div className="col-6">
                    <label className="form-label text-muted small fw-semibold">Movie ID</label>
                    <input
                      value={movieId}
                      className="form-control bg-light border-0"
                      readOnly
                    />
                  </div>
                  <div className="col-6">
                    <label className="form-label text-muted small fw-semibold">Year</label>
                    <input
                      value={movieTitle ? '—' : '—'}
                      className="form-control bg-light border-0"
                      readOnly
                    />
                  </div>
                </div>

                {/* Rating with Stars */}
                <div className="mb-4">
                  <label className="form-label fw-semibold">Your Rating <span className="text-danger">*</span></label>
                  <div className="d-flex gap-1">
                    {[1, 2, 3, 4, 5].map((star) => (
                      <button
                        key={star}
                        type="button"
                        className={`btn p-0 border-0 fs-3 lh-1 ${rating >= star ? 'text-warning' : 'text-secondary'}`}
                        onClick={() => handleStarClick(star)}
                        disabled={loading}
                        style={{ transition: 'color 0.2s' }}
                      >
                        ★
                      </button>
                    ))}
                    <span className="ms-3 text-muted small align-self-center">
                      {rating}/5
                    </span>
                  </div>
                  <input
                    type="number"
                    value={rating}
                    min="1"
                    max="5"
                    onChange={(e) => setRating(Math.max(1, Math.min(5, e.target.value)))}
                    className="form-control mt-2 d-none"
                    required
                  />
                </div>

                {/* Review Text */}
                <div className="mb-4">
                  <label className="form-label fw-semibold">Your Review <span className="text-danger">*</span></label>
                  <textarea
                    value={text}
                    onChange={(e) => setText(e.target.value)}
                    className="form-control rounded-3"
                    rows="5"
                    placeholder="What did you think of the movie? Be honest!"
                    style={{ resize: 'none' }}
                    required
                    disabled={loading}
                  />
                  <small className="text-muted">{text.length}/500</small>
                </div>

                {/* User Name */}
                <div className="mb-4">
                  <label className="form-label fw-semibold">Your Name <span className="text-danger">*</span></label>
                  <input
                    type="text"
                    value={userName}
                    onChange={(e) => setUserName(e.target.value)}
                    className="form-control rounded-3"
                    placeholder="e.g. John Doe"
                    required
                    disabled={loading}
                    maxLength="50"
                  />
                </div>

                {/* Buttons */}
                <div className="d-flex gap-3">
                  <button
                    type="submit"
                    className="btn btn-primary btn-lg flex-fill rounded-pill fw-semibold"
                    disabled={loading}
                    style={{
                      background: 'linear-gradient(45deg, #007bff, #0056b3)',
                      border: 'none',
                      boxShadow: '0 4px 12px rgba(0,123,255,0.3)',
                    }}
                  >
                    {loading ? (
                      <>
                        <span className="spinner-border spinner-border-sm me-2" role="status" aria-hidden="true"></span>
                        Submitting...
                      </>
                    ) : (
                      'Submit Review'
                    )}
                  </button>

                  <button
                    type="button"
                    className="btn btn-outline-secondary btn-lg rounded-pill"
                    onClick={() => navigate(-1)}
                    disabled={loading}
                  >
                    Cancel
                  </button>
                </div>
              </form>
            </div>
          </div>

          <p className="text-center text-muted mt-4 small">
            Your review helps others decide what to watch.
          </p>
        </div>
      </div>

      {/* Custom Hover & Focus Styles */}
      <style jsx>{`
        .card {
          transition: all 0.3s ease;
        }
        .card:hover {
          transform: translateY(-8px);
          box-shadow: 0 20px 40px rgba(0,0,0,0.1) !important;
        }
        .form-control:focus,
        .form-control:focus-visible {
          border-color: #007bff !important;
          box-shadow: 0 0 0 0.25rem rgba(0, 123, 255, 0.25) !important;
        }
        .btn-primary:hover {
          background: linear-gradient(45deg, #0056b3, #003d80) !important;
          transform: translateY(-2px);
        }
        .btn-outline-secondary:hover {
          background-color: #f8f9fa;
        }
      `}</style>
    </div>
  );
}
