import { BrowserRouter, Routes, Route, Link } from "react-router-dom";
import Home from "./pages/Home";
import Search from "./pages/Search";
import Details from "./pages/Details";
import AddReview from "./pages/AddReview";
import MyReviews from "./pages/MyReviews";

export default function App() {
  return (
    <BrowserRouter>
      <nav className="navbar navbar-expand-lg navbar-light bg-light">
        <div className="container">
          <Link className="navbar-brand" to="/">Movie Reviews</Link>
          <div className="collapse navbar-collapse">
            <ul className="navbar-nav">
              <li className="nav-item"><Link className="nav-link" to="/search">Search</Link></li>

              {/* Updated route here */}
              <li className="nav-item"><Link className="nav-link" to="/add-review">Add Review</Link></li>

              <li className="nav-item"><Link className="nav-link" to="/my-reviews">My Reviews</Link></li>
            </ul>
          </div>
        </div>
      </nav>

      <div className="container mt-4">
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/search" element={<Search />} />
          <Route path="/movie/:id" element={<Details />} />

          {/* Corrected Add Review Route */}
          <Route path="/add-review" element={<AddReview />} />

          <Route path="/my-reviews" element={<MyReviews />} />
        </Routes>
      </div>
    </BrowserRouter>
  );
}
