import React, {useState} from 'react';
import axios from 'axios';

/*
This page posts to backend: POST /api/reviews
Ensure backend is running and REACT_APP_BACKEND_URL points to it (or default http://localhost:5000)
For production, host backend and update the environment variable.
*/

export default function AddReview(){
  const [movieId,setMovieId] = useState('');
  const [movieTitle,setMovieTitle] = useState('');
  const [rating,setRating] = useState(5);
  const [text,setText] = useState('');
  const [userId,setUserId] = useState('demo-user'); // replace with auth UID in real app
  const [userName,setUserName] = useState('Demo User');

  const submit = async (e) => {
    e.preventDefault();
    const payload = { movieId, movieTitle, rating: Number(rating), text, userId, userName };
    await axios.post(`${process.env.REACT_APP_BACKEND_URL || 'http://localhost:5000'}/api/reviews`, payload);
    alert('Review submitted');
    setMovieId(''); setMovieTitle(''); setRating(5); setText('');
  };

  return (
    <div>
      <h2>Add Review</h2>
      <form onSubmit={submit}>
        <div className="mb-3">
          <label className="form-label">Movie ID (from TMDB)</label>
          <input value={movieId} onChange={e=>setMovieId(e.target.value)} className="form-control" required />
        </div>
        <div className="mb-3">
          <label className="form-label">Movie Title</label>
          <input value={movieTitle} onChange={e=>setMovieTitle(e.target.value)} className="form-control" required />
        </div>
        <div className="mb-3">
          <label className="form-label">Rating (1-5)</label>
          <input type="number" value={rating} min="1" max="5" onChange={e=>setRating(e.target.value)} className="form-control" />
        </div>
        <div className="mb-3">
          <label className="form-label">Review</label>
          <textarea value={text} onChange={e=>setText(e.target.value)} className="form-control" rows="4" />
        </div>
        <div className="mb-3">
          <label className="form-label">Your name (for demo)</label>
          <input value={userName} onChange={e=>setUserName(e.target.value)} className="form-control" />
        </div>
        <button className="btn btn-primary" type="submit">Submit Review</button>
      </form>
    </div>
  );
}
