import React, {useEffect, useState} from 'react';
import axios from 'axios';

export default function MyReviews(){
  const [reviews, setReviews] = useState([]);
  const userId = 'demo-user'; // replace with authenticated uid

  useEffect(()=>{
    axios.get(`${process.env.REACT_APP_BACKEND_URL || 'http://localhost:5000'}/api/reviews/user/${userId}`)
      .then(r=>setReviews(r.data))
      .catch(()=>setReviews([]));
  },[]);

  const remove = async (id)=>{
    if(!confirm('Delete review?')) return;
    await axios.delete(`${process.env.REACT_APP_BACKEND_URL || 'http://localhost:5000'}/api/reviews/${id}`);
    setReviews(reviews.filter(r=>r.id!==id));
  };

  return (
    <div>
      <h2>My Reviews (demo user)</h2>
      {reviews.length===0 && <p>No reviews yet.</p>}
      <ul className="list-group">
        {reviews.map(r=>(
          <li className="list-group-item" key={r.id}>
            <strong>{r.movieTitle}</strong> - {r.rating}/5
            <p>{r.text}</p>
            <button className="btn btn-sm btn-danger" onClick={()=>remove(r.id)}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
}
