import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useLocation } from 'react-router-dom';

export default function AddReview() {
  const location = useLocation();
  const queryParams = new URLSearchParams(location.search);

  // Use effect to safely set movie info from URL
  const [movieId, setMovieId] = useState('');
  const [movieTitle, setMovieTitle] = useState('');
  const [rating, setRating] = useState(5);
  const [text, setText] = useState('');
  const [userId, setUserId] = useState('demo-user');
  const [userName, setUserName] = useState('Demo User');

  useEffect(() => {
    const id = queryParams.get('movieId') || '';
    const title = queryParams.get('movieTitle') || '';
    setMovieId(id);
    setMovieTitle(title);
  }, [location.search]);

  const submit = async (e) => {
    e.preventDefault();
    try {
      const payload = { movieId, movieTitle, rating: Number(rating), text, userId, userName };
      await axios.post(
        `${process.env.REACT_APP_BACKEND_URL || 'http://localhost:5000'}/api/reviews`,
        payload
      );
      alert('Review submitted!');
      setRating(5);
      setText('');
    } catch (err) {
      console.error('Error submitting review:', err);
      alert('Failed to submit review.');
    }
  };

  return (
    <div>
      <h2>Add Review</h2>
      <form onSubmit={submit}>
        <div className="mb-3">
          <label className="form-label">Movie ID</label>
          <input value={movieId} className="form-control" readOnly />
        </div>
        <div className="mb-3">
          <label className="form-label">Movie Title</label>
          <input value={movieTitle} className="form-control" readOnly />
        </div>
        <div className="mb-3">
          <label className="form-label">Rating (1-5)</label>
          <input
            type="number"
            value={rating}
            min="1"
            max="5"
            onChange={(e) => setRating(e.target.value)}
            className="form-control"
            required
          />
        </div>
        <div className="mb-3">
          <label className="form-label">Review</label>
          <textarea
            value={text}
            onChange={(e) => setText(e.target.value)}
            className="form-control"
            rows="4"
            required
          />
        </div>
        <div className="mb-3">
          <label className="form-label">Your Name</label>
          <input
            value={userName}
            onChange={(e) => setUserName(e.target.value)}
            className="form-control"
            required
          />
        </div>
        <button className="btn btn-primary" type="submit">
          Submit Review
        </button>
      </form>
    </div>
  );
}
